import React, { useState, useEffect } from 'react';
import DOMPurify from 'isomorphic-dompurify';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { ChevronLeft, ChevronRight, Menu, Clock, Target, Trophy } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useCleanup } from '@/utils/cleanupManager';

export interface MicroLessonScreen {
  id: string;
  type: 'concept' | 'practice' | 'example' | 'summary';
  title: string;
  content: React.ReactNode | string;
  estimatedTime?: number; // in minutes
}

export interface MicroLessonData {
  id: string;
  moduleTitle: string;
  totalScreens: number;
  screens: MicroLessonScreen[];
}

interface MicroLessonContainerProps {
  lessonData: MicroLessonData;
  onComplete?: () => void;
  onNext?: () => void;
  hasNext?: boolean;
}

export const MicroLessonContainer: React.FC<MicroLessonContainerProps> = ({
  lessonData,
  onComplete,
  onNext,
  hasNext
}) => {
  const cleanup = useCleanup('micro-lesson-container');
  const [currentScreenIndex, setCurrentScreenIndex] = useState(0);
  const [startTime] = useState(Date.now());
  const [timeSpent, setTimeSpent] = useState(0);
  const [isCompleted, setIsCompleted] = useState(false);

  const currentScreen = lessonData.screens[currentScreenIndex];
  const progress = ((currentScreenIndex + 1) / lessonData.screens.length) * 100;

  // Safety check - ensure currentScreen exists
  if (!currentScreen) {
    return (
      <div className="max-w-4xl mx-auto space-y-6">
        <Card className="bg-gradient-to-r from-red-50 to-orange-50 border-red-200">
          <CardContent className="p-6 text-center">
            <p className="text-red-700">Unable to load course content. Please refresh the page.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Update time spent every second using cleanupManager
  useEffect(() => {
    cleanup.setInterval(() => {
      setTimeSpent(Math.floor((Date.now() - startTime) / 1000));
    }, 1000);
  }, [startTime, cleanup]);

  const handleNext = () => {
    if (currentScreenIndex < lessonData.screens.length - 1) {
      setCurrentScreenIndex(currentScreenIndex + 1);
    } else if (!isCompleted) {
      setIsCompleted(true);
      onComplete?.();
    }
  };

  const handlePrevious = () => {
    if (currentScreenIndex > 0) {
      setCurrentScreenIndex(currentScreenIndex - 1);
    }
  };

  const handleScreenJump = (screenIndex: number) => {
    setCurrentScreenIndex(screenIndex);
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getScreenIcon = (type: string) => {
    switch (type) {
      case 'concept': return 'ðŸ“š';
      case 'practice': return 'âœï¸';
      case 'example': return 'ðŸ’¡';
      case 'summary': return 'ðŸ“‹';
      default: return 'ðŸ“';
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header with Progress */}
      <Card className="bg-gradient-to-r from-blue-50 to-purple-50 border-blue-200">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between mb-2">
            <CardTitle className="text-xl text-blue-800 flex items-center gap-2">
              <Target className="h-5 w-5" />
              {lessonData.moduleTitle}
            </CardTitle>
            <div className="flex items-center gap-3 text-sm text-white">
              <div className="flex items-center gap-1">
                <Clock className="h-4 w-4" />
                {formatTime(timeSpent)}
              </div>
              <div className="text-sm">
                Screen {currentScreenIndex + 1} of {lessonData.screens.length}
              </div>
            </div>
          </div>
          <Progress value={progress} className="h-2" />
          <div className="flex items-center justify-between text-sm text-white mt-2">
            <span>{Math.round(progress)}% Complete</span>
            {currentScreen.estimatedTime && (
              <span>Est. {currentScreen.estimatedTime} min</span>
            )}
          </div>
        </CardHeader>
      </Card>

      {/* Main Content Screen */}
      <Card>
        <CardHeader>
          <div className="flex items-center gap-2">
            <span className="text-2xl">{getScreenIcon(currentScreen.type)}</span>
            <CardTitle className="text-xl">{currentScreen.title}</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          {typeof currentScreen.content === 'string' ? (
            <div dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(currentScreen.content) }} />
          ) : (
            currentScreen.content
          )}
        </CardContent>
      </Card>

      {/* Navigation */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center justify-between">
            <Button
              variant="outline"
              onClick={handlePrevious}
              disabled={currentScreenIndex === 0}
              className="flex items-center gap-2"
            >
              <ChevronLeft className="h-4 w-4" />
              Previous
            </Button>

            {/* Screen Navigation Menu */}
            <div className="flex gap-1 max-w-md overflow-x-auto">
              {lessonData.screens.map((screen, index) => (
                <Button
                  key={screen.id}
                  variant={index === currentScreenIndex ? "default" : "ghost"}
                  size="sm"
                  onClick={() => handleScreenJump(index)}
                  className="min-w-[40px] h-8 text-xs"
                  title={screen.title}
                >
                  {index + 1}
                </Button>
              ))}
            </div>

            {currentScreenIndex < lessonData.screens.length - 1 ? (
              <Button
                onClick={handleNext}
                className="flex items-center gap-2"
              >
                Next
                <ChevronRight className="h-4 w-4" />
              </Button>
            ) : (
              <Button
                onClick={handleNext}
                disabled={isCompleted}
                className="flex items-center gap-2"
                variant={isCompleted ? "outline" : "default"}
              >
                {isCompleted ? "Completed" : "Complete Lesson"}
              </Button>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Completion Message with Celebration */}
      {isCompleted && (
        <Card className="border-green-200 bg-gradient-to-r from-green-50 to-emerald-50">
          <CardContent className="p-8 text-center">
            <div className="space-y-6">
              <div className="animate-bounce">
                <Trophy className="h-16 w-16 text-yellow-500 mx-auto" />
              </div>
              <div className="space-y-2">
                <h3 className="text-2xl font-bold text-green-700">ðŸŽ‰ Lesson Completed! ðŸŽ‰</h3>
                <p className="text-green-600 font-medium">
                  <strong>Amazing work!</strong> You've mastered all the concepts in this lesson.
                </p>
                <p className="text-sm text-green-600">
                  Total time: {formatTime(timeSpent)}
                </p>
              </div>
              {hasNext && onNext && (
                <Button 
                  onClick={onNext} 
                  size="lg" 
                  className="bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white shadow-lg transform hover:scale-105 transition-all duration-200"
                >
                  Continue to Next Lesson
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

import React, { useState, useReducer, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { 
  DollarSign, 
  TrendingUp, 
  TrendingDown, 
  PiggyBank, 
  CreditCard, 
  Target,
  AlertCircle,
  Trophy,
  Clock,
  BookOpen,
  Calculator,
  BarChart3,
  Receipt,
  Minus,
  Plus,
  CheckCircle,
  XCircle,
  ArrowRight,
  Lightbulb,
  TrendingDown as TrendDown,
  TrendingUp as TrendUp
} from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { gameReducer, initialState } from './game-state';
import { ALL_SCENARIOS } from './game-data';

interface GameProps {
  onComplete?: () => void;
  onNext?: () => void;
  hasNext?: boolean;
  isCompleted?: boolean;
  trackInteraction?: (event: string, details: any) => void;
  lessonId?: number;
  lessonTitle?: string;
}

const MoneyManagementGame: React.FC<GameProps> = ({
  onComplete,
  onNext,
  hasNext = false,
  isCompleted = false,
  trackInteraction,
  lessonId,
  lessonTitle
}) => {
  const [state, dispatch] = useReducer(gameReducer, initialState);
  const [showWelcome, setShowWelcome] = useState(true);
  const [dragAmount, setDragAmount] = useState(10);
  const [ccPaymentAmount, setCcPaymentAmount] = useState('');
  const [debtPaymentAmount, setDebtPaymentAmount] = useState('');
  const [outcomeDelayComplete, setOutcomeDelayComplete] = useState(false);

  const {
    phase,
    week,
    balance,
    savingsGoal,
    creditScore,
    envelopes,
    monthlyIncome,
    currentSavings,
    currentEmergencyFund,
    score,
    isGameOver,
    currentScenario,
    feedbackMessage,
    month,
    currentDebt,
    creditCardBalance,
    creditCardLimit,
    creditCardInterestRate,
    debtInterestRate
  } = state;

  // Track game interactions
  const trackGameInteraction = (action: string, details: any = {}) => {
    trackInteraction?.(action, {
      lessonId,
      lessonTitle: "Financial Decision-Making Game",
      gamePhase: phase,
      week,
      month,
      ...details
    });
  };

  // Welcome Screen Component
  const WelcomeScreen = () => (
    <div className="max-w-2xl mx-auto text-center space-y-6 p-8">
      <div className="flex justify-center items-center gap-3 mb-6">
        <DollarSign className="w-16 h-16 text-primary" />
        <Trophy className="w-16 h-16 text-yellow-500" />
        <TrendingUp className="w-16 h-16 text-green-500" />
      </div>
      
      <h1 className="text-4xl font-bold text-foreground">
        Money Management Game
      </h1>
      
      <p className="text-xl text-muted-foreground leading-relaxed">
        Put your financial knowledge to the test! Make real-world money decisions, 
        manage your budget, and see how your choices impact your financial future.
      </p>

      <div className="bg-card/50 backdrop-blur-sm p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4 text-foreground">Game Overview</h3>
        <ul className="text-left space-y-2 text-muted-foreground">
          <li className="flex items-start gap-2">
            <Target className="w-5 h-5 text-primary mt-0.5 flex-shrink-0" />
            <span>Set your monthly budget and financial goals</span>
          </li>
          <li className="flex items-start gap-2">
            <BookOpen className="w-5 h-5 text-primary mt-0.5 flex-shrink-0" />
            <span>Make smart decisions in real-world scenarios</span>
          </li>
          <li className="flex items-start gap-2">
            <PiggyBank className="w-5 h-5 text-primary mt-0.5 flex-shrink-0" />
            <span>Build savings and manage debt wisely</span>
          </li>
          <li className="flex items-start gap-2">
            <Trophy className="w-5 h-5 text-primary mt-0.5 flex-shrink-0" />
            <span>Achieve financial stability and reach your goals</span>
          </li>
        </ul>
      </div>

      <Button 
        size="lg" 
        onClick={() => {
          setShowWelcome(false);
          trackGameInteraction('game_started');
        }}
        className="bg-primary hover:bg-primary/90 text-primary-foreground font-semibold py-3 px-8"
      >
        Start Your Financial Journey
      </Button>
    </div>
  );

  const BudgetPlanningScreen = () => {
    const remainingBudget = monthlyIncome - Object.values(envelopes).reduce((sum, val) => sum + val, 0) - (state.creditCardPayment || 0);
    const weeklyBudgets = Object.entries(envelopes).reduce((acc, [category, amount]) => {
      acc[category] = (amount / 4).toFixed(2); // Weekly allocation
      return acc;
    }, {} as Record<string, string>);

    const BudgetControls = ({ category, amount }: { category: string; amount: number }) => {
      const increments = [1, 10, 25, 50];
      
      return (
        <div className="bg-muted rounded-lg p-4 space-y-3">
          <div className="flex items-center justify-between">
            <span className="font-medium">{category}</span>
            <div className="text-lg font-bold text-primary">
              ${amount.toFixed(2)}
            </div>
          </div>
          
          <div className="text-sm text-muted-foreground">
            Weekly: ${category === 'Credit Card Payment' ? (amount / 4).toFixed(2) : weeklyBudgets[category]}
          </div>
          
          <div className="flex flex-wrap gap-2">
            {/* Subtract buttons */}
            <div className="flex gap-1">
              {increments.map(inc => (
                <Button
                  key={`sub-${inc}`}
                  size="sm"
                  variant="outline"
                  onClick={() => {
                    if (amount >= inc) {
                      if (category === 'Credit Card Payment') {
                        dispatch({ type: 'ALLOCATE_BUDGET', category, amount: amount - inc });
                      } else {
                        dispatch({ type: 'ALLOCATE_BUDGET', category, amount: amount - inc });
                      }
                    }
                  }}
                  disabled={amount < inc}
                  className="text-xs px-2"
                >
                  -${inc}
                </Button>
              ))}
            </div>
            
            <div className="w-px bg-border"></div>
            
            {/* Add buttons */}
            <div className="flex gap-1">
              {increments.map(inc => (
                <Button
                  key={`add-${inc}`}
                  size="sm"
                  variant="outline"
                  onClick={() => {
                    if (remainingBudget >= inc) {
                      if (category === 'Credit Card Payment') {
                        dispatch({ type: 'ALLOCATE_BUDGET', category, amount: amount + inc });
                      } else {
                        dispatch({ type: 'ALLOCATE_BUDGET', category, amount: amount + inc });
                      }
                    }
                  }}
