      if (balanceChange !== 0) {
        impacts.push({
          type: 'Balance',
          amount: balanceChange,
          icon: balanceChange > 0 ? TrendUp : TrendDown,
          color: balanceChange > 0 ? 'text-green-600' : 'text-red-600'
        });
