            <CardTitle className="text-2xl flex items-center gap-2">
              <BarChart3 className="w-6 h-6 text-green-600" />
              End-of-Week Recap - Week {week}
            </CardTitle>
          </CardHeader>
        </Card>

        {/* Weekly Analytics Tiles */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
