                                 const canAdd = newAmount <= balance && newAmount <= creditCardBalance;
                                 return (
                                   <Button
                                     key={`add-${inc}`}
                                     size="sm"
                                     variant="outline"
                                     onClick={() => setCcPaymentAmount((newAmount).toString())}
                                     disabled={!canAdd}
                                     className="text-xs px-2"
                                   >
                                     +${inc}
                                   </Button>
                                 );
                               })}
                             </div>
                             
                             <div className="flex gap-1">
                               {[1, 5, 10, 25, 50].map(inc => {
                                 const currentAmount = parseFloat(ccPaymentAmount || '0');
                                 const newAmount = Math.max(0, currentAmount - inc);
                                 return (
                                   <Button
                                     key={`sub-${inc}`}
                                     size="sm"
                                     variant="outline"
                                     onClick={() => setCcPaymentAmount(newAmount.toString())}
                                     disabled={currentAmount < inc}
                                     className="text-xs px-2"
                                   >
                                     -${inc}
                                   </Button>
                                 );
                               })}
                             </div>
                             
                             <Button
                               variant="outline"
                               size="sm"
                               onClick={() => setCcPaymentAmount('0')}
                               disabled={!ccPaymentAmount || parseFloat(ccPaymentAmount) === 0}
                               className="text-xs"
                             >
                               Clear
                             </Button>
                             
                             <Button
                               variant="outline"
                               size="sm"
                               onClick={() => setCcPaymentAmount(Math.min(balance, creditCardBalance).toString())}
                               className="text-xs"
                             >
                               Max
                             </Button>
                             
                             <Button
                               variant="default"
                               onClick={() => {
                                 const amount = parseFloat(ccPaymentAmount);
                                 if (amount > 0) {
                                   handleDebtPayment('credit', amount);
                                   setCcPaymentAmount('');
                                 }
                               }}
                               disabled={!ccPaymentAmount || parseFloat(ccPaymentAmount) <= 0 || parseFloat(ccPaymentAmount) > balance}
                             >
                               Pay ${ccPaymentAmount || '0'}
                             </Button>
                           </div>
                         </div>
                        {ccPaymentAmount && parseFloat(ccPaymentAmount) > 0 && (
                          <div className="bg-gray-50 p-3 rounded text-sm">
                            {(() => {
                              const payment = parseFloat(ccPaymentAmount);
                              const calc = calculatePayoffTime(creditCardBalance, payment, creditCardInterestRate);
                              return (
                                <div>
                                  <div>Payoff time: <strong>{calc.months} months</strong></div>
                                  <div>Total interest: <strong>${calc.totalInterest.toFixed(2)}</strong></div>
                                </div>
                              );
                            })()}
                          </div>
                        )}
                      </div>
                    </div>
                  )}

                  {currentDebt > 0 && (
                    <div className="space-y-4">
                      <h5 className="font-medium">Other Debt Payment</h5>
                       <div className="space-y-3">
                         <div>
                           <Label>Payment Amount</Label>
                           <div className="flex items-center gap-2 mt-1">
                             <div className="text-sm text-muted-foreground">
                               Current Amount: ${debtPaymentAmount || '0'}
                             </div>
                           </div>
                           
                           {/* Increment buttons */}
                           <div className="flex flex-wrap gap-2 mt-2">
                             <div className="flex gap-1">
                               {[1, 5, 10, 25, 50].map(inc => {
                                 const newAmount = parseFloat(debtPaymentAmount || '0') + inc;
                                 const canAdd = newAmount <= balance && newAmount <= currentDebt;
                                 return (
                                   <Button
                                     key={`add-${inc}`}
                                     size="sm"
                                     variant="outline"
                                     onClick={() => setDebtPaymentAmount((newAmount).toString())}
                                     disabled={!canAdd}
                                     className="text-xs px-2"
                                   >
                                     +${inc}
                                   </Button>
                                 );
                               })}
                             </div>
                             
                             <div className="flex gap-1">
                               {[1, 5, 10, 25, 50].map(inc => {
                                 const currentAmount = parseFloat(debtPaymentAmount || '0');
                                 const newAmount = Math.max(0, currentAmount - inc);
                                 return (
                                   <Button
                                     key={`sub-${inc}`}
                                     size="sm"
                                     variant="outline"
                                     onClick={() => setDebtPaymentAmount(newAmount.toString())}
                                     disabled={currentAmount < inc}
                                     className="text-xs px-2"
                                   >
                                     -${inc}
                                   </Button>
                                 );
                               })}
                             </div>
                             
                             <Button
                               variant="outline"
                               size="sm"
                               onClick={() => setDebtPaymentAmount('0')}
                               disabled={!debtPaymentAmount || parseFloat(debtPaymentAmount) === 0}
                               className="text-xs"
                             >
                               Clear
                             </Button>
                             
                             <Button
                               variant="outline"
                               size="sm"
                               onClick={() => setDebtPaymentAmount(Math.min(balance, currentDebt).toString())}
                               className="text-xs"
                             >
                               Max
                             </Button>
                             
                             <Button
                               variant="default"
                               onClick={() => {
                                 const amount = parseFloat(debtPaymentAmount);
                                 if (amount > 0) {
                                   handleDebtPayment('debt', amount);
                                   setDebtPaymentAmount('');
                                 }
                               }}
                               disabled={!debtPaymentAmount || parseFloat(debtPaymentAmount) <= 0 || parseFloat(debtPaymentAmount) > balance}
                             >
                               Pay ${debtPaymentAmount || '0'}
                             </Button>
                           </div>
                         </div>
                        {debtPaymentAmount && parseFloat(debtPaymentAmount) > 0 && (
                          <div className="bg-gray-50 p-3 rounded text-sm">
                            {(() => {
                              const payment = parseFloat(debtPaymentAmount);
                              const calc = calculatePayoffTime(currentDebt, payment, debtInterestRate);
                              return (
                                <div>
                                  <div>Payoff time: <strong>{calc.months} months</strong></div>
                                  <div>Total interest: <strong>${calc.totalInterest.toFixed(2)}</strong></div>
                                </div>
                              );
                            })()}
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Enhanced Financial Activity Log */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Receipt className="w-6 h-6" />
              Financial Activity Log - Week {week}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {state.weekLog.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No transactions recorded this week.
                </div>
              ) : (
                state.weekLog.map((entry, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-muted rounded-lg hover:bg-muted/80 transition-colors">
                    <div className="flex-1">
                      <div className="font-medium">{entry.title || entry.type}</div>
                      <div className="text-sm text-muted-foreground mt-1">{entry.option || entry.note}</div>
                      {entry.impact && entry.impact.includes('creditCard') && (
                        <div className="text-xs text-red-600 mt-1 flex items-center gap-1">
                          <CreditCard className="w-3 h-3" />
                          This purchase will accrue interest on your credit card
                        </div>
                      )}
                      <div className="text-xs text-muted-foreground mt-1">
                        {entry.date} â€¢ Week {entry.week}
                      </div>
                    </div>
                    <div className="text-right ml-4">
                      <div className={`text-lg font-bold ${entry.amount >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {entry.amount >= 0 ? '+' : ''}${entry.amount?.toFixed(2) || '0.00'}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        Balance: ${entry.balanceAfter?.toFixed(2)}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </CardContent>
        </Card>

        {/* Current Financial Status */}
        <Card className="bg-gradient-to-r from-blue-50 to-purple-50 border-blue-200">
          <CardContent className="pt-6">
            <h3 className="text-lg font-semibold mb-4">Current Financial Status</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">${balance.toFixed(2)}</div>
                <div className="text-sm text-muted-foreground">Current Balance</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">{score}</div>
                <div className="text-sm text-muted-foreground">Financial Score</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600">{creditScore}</div>
                <div className="text-sm text-muted-foreground">Credit Score</div>
              </div>
              <div className="text-center">
                <div className={`text-2xl font-bold ${(creditCardBalance + currentDebt) > 0 ? 'text-red-600' : 'text-green-600'}`}>
                  ${(creditCardBalance + currentDebt).toFixed(2)}
                </div>
                <div className="text-sm text-muted-foreground">Total Debt</div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Game Loop Integration */}
        <Card>
          <CardContent className="pt-6">
            <div className="text-center space-y-4">
              <h3 className="text-lg font-semibold">
                {week > 4 ? 'Ready for Monthly Summary?' : `Ready for Week ${week + 1}?`}
              </h3>
              <p className="text-muted-foreground">
                {week > 4 
                  ? 'You\'ve completed all four weeks! View your monthly report to see how you did overall and reflect on your financial journey.'
                  : 'Review your performance above, then continue to the next week when you\'re ready to face new financial challenges.'
                }
              </p>
              <Button 
                size="lg" 
                className="w-full max-w-md mx-auto"
                onClick={() => {
                  console.log('WeeklySummary Button clicked - Week:', week, 'Should end game?', week > 4);
                  if (week > 4) {
                    dispatch({ type: 'GAME_OVER', status: 'completed' });
                    trackGameInteraction('game_completed', { 
                      finalScore: score, 
                      finalBalance: balance,
                      weeks: week 
                    });
                  } else {
                    dispatch({ type: 'NEXT_WEEK' });
                    trackGameInteraction('week_completed', { week, score, balance });
                  }
                }}
              >
                {week > 4 ? 'ðŸ“Š View Monthly Summary' : `âž¡ï¸ Go to Week ${week + 1}`}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  };

  // Game Over/Reflection Screen (only shows after 4 complete weeks)
  const ReflectionScreen = () => {
    // Only show reflection if we've truly completed all 4 weeks
    const isActuallyComplete = week > 4 || (week === 4 && phase === 'REFLECT' && isGameOver);
    
    if (!isActuallyComplete) {
      // If we're here incorrectly, redirect back to appropriate phase
      console.log('ReflectionScreen called prematurely - redirecting. Week:', week, 'Phase:', phase, 'GameOver:', isGameOver);
      
      // Determine correct phase based on current state
      if (week <= 4 && state.scenarioIndex < state.weeklyScenarios.length) {
        // Should be in LIVE phase for more scenarios
        return <ScenarioScreen />;
      } else if (week <= 4) {
        // Should be in WEEKLY_SUMMARY 
        return <WeeklySummaryScreen />;
      }
    }

    return (
      <div className="max-w-4xl mx-auto space-y-6 text-center">
        <Card className="bg-gradient-to-r from-purple-50 to-pink-50 border-purple-200">
          <CardHeader>
            <div className="flex justify-center mb-4">
              <Trophy className="w-20 h-20 text-yellow-500" />
            </div>
            <CardTitle className="text-3xl">4-Week Game Complete!</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <p className="text-xl text-muted-foreground">
              Congratulations! You've completed all 4 weeks of the Money Management Game and gained practical experience with real-world financial decisions.
            </p>

            <div className="bg-card p-4 rounded-lg border">
              <h4 className="font-semibold mb-2">Game Summary</h4>
              <div className="text-sm text-muted-foreground">
                Completed {week > 4 ? 4 : week} weeks â€¢ {Math.max(0, (week - 1) * 5 + state.scenarioIndex)} scenarios faced
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="p-4 bg-card rounded-lg border">
                <div className="text-2xl font-bold text-green-600">${balance.toFixed(2)}</div>
                <div className="text-sm text-muted-foreground">Final Balance</div>
              </div>
              <div className="p-4 bg-card rounded-lg border">
                <div className="text-2xl font-bold text-blue-600">{score}</div>
                <div className="text-sm text-muted-foreground">Financial Wisdom Score</div>
              </div>
              <div className="p-4 bg-card rounded-lg border">
                <div className="text-2xl font-bold text-purple-600">{creditScore}</div>
                <div className="text-sm text-muted-foreground">Credit Score</div>
              </div>
            </div>

            <div className="bg-card p-6 rounded-lg border">
              <h3 className="text-lg font-semibold mb-4">Key Takeaways</h3>
              <ul className="text-left space-y-2 text-muted-foreground">
                <li className="flex items-start gap-2">
                  <span className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></span>
                  <span>Smart budgeting helps you make informed financial decisions</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></span>
                  <span>Emergency funds protect you from unexpected expenses</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></span>
                  <span>Every financial choice has long-term consequences</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></span>
                  <span>Building good credit habits early pays off over time</span>
                </li>
              </ul>
            </div>

            {/* Only call onComplete if we've actually finished all 4 weeks */}
            <Button 
              size="lg" 
              onClick={() => {
                // Double check we're actually complete before calling onComplete
                if (week > 4 || (week === 4 && isGameOver)) {
                  onComplete?.();
                  trackGameInteraction('lesson_completed', { 
                    finalScore: score, 
                    finalBalance: balance,
                    weeksCompleted: week > 4 ? 4 : week
                  });
                } else {
                  console.warn('Attempted to complete lesson prematurely');
                  // Force back to correct state
                  dispatch({ type: 'NEXT_WEEK' });
                }
              }}
              className="bg-primary hover:bg-primary/90"
            >
              Complete Lesson
            </Button>

            {hasNext && (
              <Button 
                size="lg" 
                variant="outline" 
                onClick={onNext}
                className="ml-4"
              >
                Continue to Next Lesson
              </Button>
            )}
          </CardContent>
        </Card>
      </div>
    );
  };

  // Main render logic with debugging
  if (showWelcome) {
    return <WelcomeScreen />;
  }

  // Debug logging to understand game state
  console.log('Game render state:', { 
    phase, 
    week, 
    scenarioIndex: state.scenarioIndex, 
    weeklyScenarios: state.weeklyScenarios?.length || 0,
    hasScenarios: !!state.weeklyScenarios?.length,
    currentScenario: state.currentScenario?.title || 'none',
    isGameOver 
  });

  if (phase === 'PLAN') {
    return <BudgetPlanningScreen />;
  }

  if (phase === 'LIVE') {
    return <ScenarioScreen />;
  }

  if (phase === 'SCENARIO_OUTCOME') {
    return <ScenarioOutcomeSummaryScreen />;
  }

  if (phase === 'WEEKLY_SUMMARY') {
    return <WeeklySummaryScreen />;
  }

  // Only go to reflection screen if we've actually completed 4 weeks
  if (phase === 'REFLECT' && (week > 4 || isGameOver)) {
    return <ReflectionScreen />;
  }

  // If we somehow get into an invalid state, reset to appropriate phase
  if (phase === 'REFLECT' && week <= 4) {
    console.warn('Invalid state: REFLECT phase with week <= 4. Redirecting to appropriate phase.');
    // Force back to correct phase
    if (state.scenarioIndex < state.weeklyScenarios.length) {
      return <ScenarioScreen />;
    } else {
      return <WeeklySummaryScreen />;
    }
  }

  return <div>Loading game... (Week: {week}, Phase: {phase})</div>;
};

// Main lesson component - direct game without container wrapper
export const MoneyManagementGameMicroLesson: React.FC<GameProps> = (props) => {
  return <MoneyManagementGame {...props} />;
};

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import DOMPurify from 'isomorphic-dompurify';

interface QuizOption {
  id: string;
  text: string;
}

interface MCQAnswerKey {
  correct_answer: string;
  explanation?: string;
}

interface MultiAnswerKey {
  correct_answers: string[];
  explanation?: string;
}

interface NumericAnswerKey {
  correct_value: number;
  explanation?: string;
}

interface QuizItem {
  id: string;
  kind: 'mcq' | 'multi' | 'numeric';
  prompt: string;
  options_json: QuizOption[];
  answer_key_json: MCQAnswerKey | MultiAnswerKey | NumericAnswerKey;
  points: number;
  order_index: number;
}

interface BlockDataRichText {
  html: string;
}

interface BlockDataImage {
  src: string;
  alt?: string;
  caption?: string;
}

interface LessonBlock {
  id: string;
  type: 'rich-text' | 'image' | 'legacy-html' | 'quiz';
  order_index: number;
  data_json: BlockDataRichText | BlockDataImage | Record<string, unknown>;
  quiz_items?: QuizItem[];
}

interface LessonData {
  id: string;
  title: string;
  lesson_blocks: LessonBlock[];
}

interface NativeLessonRendererProps {
  lesson: LessonData;
}

export function NativeLessonRenderer({ lesson }: NativeLessonRendererProps) {
  // Sort blocks by order_index
  const sortedBlocks = lesson.lesson_blocks?.sort((a, b) => a.order_index - b.order_index) || [];

  const renderBlock = (block: LessonBlock) => {
    const data = block.data_json as Record<string, any>;
    switch (block.type) {
      case 'rich-text':
        return (
          <Card key={block.id} className="mb-6">
            <CardContent className="pt-6">
              <div 
                className="prose prose-slate max-w-none"
                dangerouslySetInnerHTML={{
                  __html: DOMPurify.sanitize(data.html || '', {
                    ALLOWED_TAGS: ['h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'p', 'br', 'strong', 'em', 'u', 'ol', 'ul', 'li', 'a', 'img', 'div', 'span', 'table', 'tbody', 'tr', 'td', 'th'],
                    ALLOWED_ATTR: ['href', 'src', 'alt', 'title', 'class', 'id', 'style']
                  })
                }}
              />
            </CardContent>
          </Card>
        );

      case 'image':
        return (
          <Card key={block.id} className="mb-6">
            <CardContent className="pt-6 text-center">
              <img
                src={data.src}
                alt={data.alt || 'Course image'}
                className="max-w-full h-auto mx-auto rounded-lg shadow-sm"
              />
              {data.caption && (
                <p className="text-sm text-muted-foreground mt-2">
                  {data.caption}
                </p>
              )}
            </CardContent>
          </Card>
        );

      case 'legacy-html':
        const proxyUrl = `/native-content/${data.packageId}/${data.src}`;
        return (
          <Card key={block.id} className="mb-6">
            <CardHeader>
              <CardTitle className="text-lg flex items-center">
                Interactive Content
                <Badge variant="secondary" className="ml-2">Legacy</Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <iframe
                src={proxyUrl}
                className="w-full h-96 border rounded-lg"
                sandbox="allow-scripts allow-forms allow-same-origin allow-pointer-lock allow-popups"
                title="Interactive lesson content"
              />
            </CardContent>
          </Card>
        );

      case 'quiz':
        return (
          <Card key={block.id} className="mb-6 border-primary/20">
            <CardHeader>
              <CardTitle className="text-lg flex items-center">
                Knowledge Check
                <Badge variant="default" className="ml-2 fpk-gradient text-white">Quiz</Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <QuizRenderer items={block.quiz_items || []} />
            </CardContent>
          </Card>
        );

      default:
        return null;
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">{lesson.title}</h1>
        <div className="flex items-center space-x-2 text-sm text-muted-foreground">
          <Badge variant="outline">{sortedBlocks.length} sections</Badge>
          <span>â€¢</span>
          <span>Interactive Lesson</span>
        </div>
      </div>

      <div className="space-y-6">
        {sortedBlocks.map(renderBlock)}
      </div>
    </div>
  );
}

// Quiz renderer component
function QuizRenderer({ items }: { items: QuizItem[] }) {
  const [answers, setAnswers] = React.useState<{ [key: string]: string | string[] | number }>({});
  const [submitted, setSubmitted] = React.useState(false);

  const handleAnswerChange = (itemId: string, value: string | string[] | number) => {
    setAnswers(prev => ({ ...prev, [itemId]: value }));
  };

  const handleSubmit = () => {
    setSubmitted(true);
    // Here you would typically send answers to the server
    console.log('Quiz answers:', answers);
  };

  const sortedItems = items.sort((a, b) => a.order_index - b.order_index);

  return (
    <div className="space-y-6">
      {sortedItems.map((item, index) => (
        <div key={item.id} className="border rounded-lg p-4">
          <h4 className="font-semibold mb-3">
            Question {index + 1}: {item.prompt}
          </h4>

          {item.kind === 'mcq' && (
            <div className="space-y-2">
              {item.options_json.map((option: QuizOption) => (
                <label key={option.id} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="radio"
                    name={`question-${item.id}`}
                    value={option.id}
                    onChange={() => handleAnswerChange(item.id, option.id)}
                    disabled={submitted}
                    className="form-radio"
                  />
                  <span>{option.text}</span>
                </label>
              ))}
            </div>
          )}

          {item.kind === 'multi' && (
            <div className="space-y-2">
              {item.options_json.map((option: QuizOption) => (
                <label key={option.id} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    value={option.id}
                    onChange={(e) => {
                      const current = (answers[item.id] as string[]) || [];
                      if (e.target.checked) {
                        handleAnswerChange(item.id, [...current, option.id]);
                      } else {
                        handleAnswerChange(item.id, current.filter((id: string) => id !== option.id));
                      }
                    }}
                    disabled={submitted}
                    className="form-checkbox"
                  />
                  <span>{option.text}</span>
                </label>
              ))}
            </div>
          )}

          {item.kind === 'numeric' && (
            <input
              type="number"
              step="any"
              onChange={(e) => handleAnswerChange(item.id, parseFloat(e.target.value))}
              disabled={submitted}
              className="w-full max-w-xs p-2 border rounded"
              placeholder="Enter your answer"
            />
          )}

          {submitted && (
            <div className="mt-3 p-3 rounded-lg bg-muted/50 text-sm">
              <div className="flex items-center space-x-2 mb-2">
                <Badge variant={
                  // Check if answers match for different quiz types
                  (() => {
                    const answerKey = item.answer_key_json as any;
                    const userAnswer = answers[item.id];
                    
                    if (item.kind === 'mcq') {
                      return userAnswer === answerKey?.correct_answer;
                    } else if (item.kind === 'multi') {
                      const userAnswers = Array.isArray(userAnswer) ? userAnswer : [];
                      const correctAnswers = answerKey?.correct_answers || [];
                      return JSON.stringify(userAnswers.sort()) === JSON.stringify(correctAnswers.sort());
                    } else if (item.kind === 'numeric') {
                      const userNum = typeof userAnswer === 'number' ? userAnswer : 0;
                      const correctNum = answerKey?.correct_value || 0;
                      return Math.abs(userNum - correctNum) < 0.001;
                    }
                    return false;
                  })() ? 'default' : 'destructive'
                }>
                  {(() => {
                    const answerKey = item.answer_key_json as any;
                    const userAnswer = answers[item.id];
                    
                    if (item.kind === 'mcq') {
                      return userAnswer === answerKey?.correct_answer ? 'Correct' : 'Incorrect';
                    } else if (item.kind === 'multi') {
                      const userAnswers = Array.isArray(userAnswer) ? userAnswer : [];
                      const correctAnswers = answerKey?.correct_answers || [];
                      return JSON.stringify(userAnswers.sort()) === JSON.stringify(correctAnswers.sort()) ? 'Correct' : 'Incorrect';
                    } else if (item.kind === 'numeric') {
                      const userNum = typeof userAnswer === 'number' ? userAnswer : 0;
                      const correctNum = answerKey?.correct_value || 0;
                      return Math.abs(userNum - correctNum) < 0.001 ? 'Correct' : 'Incorrect';
                    }
                    return 'Incorrect';
                  })()}
                </Badge>
                <span className="text-xs text-muted-foreground">
                  {item.points} points
                </span>
              </div>
              {(item.answer_key_json as any)?.explanation && (
                <p className="text-sm text-muted-foreground">
                  <strong>Explanation:</strong> {(item.answer_key_json as any).explanation}
                </p>
              )}
            </div>
          )}
        </div>
      ))}

      {!submitted && (
        <button
          onClick={handleSubmit}
          className="w-full py-2 px-4 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors"
        >
          Submit Quiz
        </button>
      )}
    </div>
  );
}

import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Upload, FileText, Image, Film } from 'lucide-react';
import { useKnowledgeBaseFiles } from '@/hooks/useKnowledgeBaseFiles';
import { useBatchProcessing } from '@/hooks/useBatchProcessing';
import BatchProcessingPanel from './BatchProcessingPanel';

const allowedTypes = [
  'application/pdf',
  'application/msword',
  'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'application/vnd.ms-powerpoint',
  'application/vnd.openxmlformats-officedocument.presentationml.presentation',
  'text/plain',
  'text/markdown',
  'text/csv',
  'application/rtf',
  'image/png',
  'image/jpeg',
  'image/gif',
  'image/webp'
];

const maxFileSize = 50 * 1024 * 1024; // 50MB

const formatFileSize = (bytes: number): string => {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

const getFileIcon = (mimeType: string) => {
  if (mimeType.startsWith('image/')) return Image;
  if (mimeType.startsWith('video/')) return Film;
  return FileText;
};

const KBUploader: React.FC = () => {
  const { uploadAndProcessFile } = useKnowledgeBaseFiles();
  const {
    batchState,
    addFiles,
    updateFileStatus,
    processBatch,
    pauseProcessing,
    cancelProcessing,
    clearBatch,
    removeFile,
    isProcessing
  } = useBatchProcessing();

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const validFiles = acceptedFiles.filter(file => {
      if (!allowedTypes.includes(file.type)) {
        return false;
      }
      
      if (file.size > maxFileSize) {
        return false;
      }

      return true;
    });

    if (validFiles.length > 0) {
      addFiles(validFiles);
    }
  }, [addFiles]);

  const handleStartProcessing = useCallback(async () => {
    await processBatch(async (file: File, fileId: string) => {
      try {
        updateFileStatus(fileId, 'processing', 50);
        await uploadAndProcessFile(file);
        updateFileStatus(fileId, 'completed', 100);
      } catch (error) {
        throw error;
      }
    });
  }, [processBatch, updateFileStatus, uploadAndProcessFile]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/vnd.ms-powerpoint': ['.ppt'],
      'application/vnd.openxmlformats-officedocument.presentationml.presentation': ['.pptx'],
      'text/plain': ['.txt'],
      'text/markdown': ['.md'],
      'text/csv': ['.csv'],
      'application/rtf': ['.rtf'],
      'image/png': ['.png'],
      'image/jpeg': ['.jpg', '.jpeg'],
      'image/gif': ['.gif'],
      'image/webp': ['.webp']
    },
    maxSize: maxFileSize,
    multiple: true,
    disabled: isProcessing
  });

  return (
    <div className="space-y-4">
      <Card className="border-2 border-dashed transition-colors duration-200 hover:border-primary/50">
        <CardContent className="p-6">
          <div 
            {...getRootProps()} 
            className={`cursor-pointer text-center space-y-4 ${
              isDragActive ? 'opacity-75' : ''
            } ${isProcessing ? 'pointer-events-none opacity-50' : ''}`}
          >
            <input {...getInputProps()} />
            
            <div className="flex justify-center">
              <Upload className={`h-12 w-12 transition-colors ${
                isDragActive ? 'text-primary' : 'text-muted-foreground'
              }`} />
            </div>
            
            <div>
              <h3 className="text-lg font-semibold mb-2">
                {isDragActive ? 'Drop files here' : 'Upload Knowledge Base Files'}
              </h3>
              <p className="text-sm text-muted-foreground mb-4">
                Drag & drop files or click to browse. These will enhance your AI conversations.
              </p>
              
              <div className="space-y-2 text-xs text-muted-foreground">
                <div>
                  <strong>Supported:</strong> PDF, DOC, DOCX, PPT, PPTX, TXT, MD, CSV, RTF, Images
                </div>
                <div>
                  <strong>Max size:</strong> {formatFileSize(maxFileSize)}
                </div>
              </div>
            </div>

            <Button
              type="button"
              variant="outline"
              className="w-full"
              disabled={isProcessing}
            >
              {isProcessing ? 'Processing...' : 'Choose Files'}
            </Button>
          </div>
        </CardContent>
      </Card>

      <BatchProcessingPanel
        batchState={batchState}
        onStart={handleStartProcessing}
        onPause={pauseProcessing}
        onCancel={cancelProcessing}
        onClear={clearBatch}
        onRemoveFile={removeFile}
        isProcessing={isProcessing}
      />
    </div>
  );
};

export default KBUploader;

import React, { createContext, useContext, useEffect, useState } from 'react';
import { useOrgContext } from '@/components/organizations/OrgContext';
import { supabase } from '@/integrations/supabase/client';

interface OrgBranding {
  logo_url?: string;
  banner_url?: string;
  primary_color?: string;
  accent_color?: string;
}

interface OrgThemeContextType {
  branding: OrgBranding | null;
  isLoading: boolean;
}

const OrgThemeContext = createContext<OrgThemeContextType>({
  branding: null,
  isLoading: true,
});

export function useOrgTheme() {
  return useContext(OrgThemeContext);
}

interface OrgThemeProviderProps {
  children: React.ReactNode;
}

export function OrgThemeProvider({ children }: OrgThemeProviderProps) {
  const { currentOrg } = useOrgContext();
  const [branding, setBranding] = useState<OrgBranding | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!currentOrg) {
      setBranding(null);
      setIsLoading(false);
      return;
    }

    const loadBranding = async () => {
      try {
        const { data, error } = await supabase
          .from('organizations')
          .select('branding')
          .eq('id', currentOrg.organization_id)
          .single();

        if (error) {
          console.error('Error loading org branding:', error);
          setBranding(null);
        } else {
          setBranding((data?.branding as OrgBranding) || null);
        }
      } catch (error) {
        console.error('Error loading org branding:', error);
        setBranding(null);
      } finally {
        setIsLoading(false);
      }
    };

    loadBranding();
  }, [currentOrg]);

  // Apply branding CSS variables
  useEffect(() => {
    const root = document.documentElement;
    
    if (branding) {
      if (branding.primary_color) {
        root.style.setProperty('--org-primary', branding.primary_color);
      }
      if (branding.accent_color) {
        root.style.setProperty('--org-accent', branding.accent_color);
      }
    } else {
      // Reset to defaults when no branding
      root.style.removeProperty('--org-primary');
      root.style.removeProperty('--org-accent');
    }

    return () => {
      // Cleanup on unmount
      root.style.removeProperty('--org-primary');
      root.style.removeProperty('--org-accent');
    };
  }, [branding]);

  return (
    <OrgThemeContext.Provider value={{ branding, isLoading }}>
      {children}
    </OrgThemeContext.Provider>
  );
}

import React, { createContext, useContext, useEffect, useState } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { useUserOrganizations } from '@/hooks/useUserOrganization';
import type { UserOrganizationMembership } from '@/hooks/useUserOrganization';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { useOrgBranding } from '@/hooks/useOrgBranding';
import { safeLocalStorage } from '@/utils/safeStorage';

interface OrgContextType {
  currentOrg: UserOrganizationMembership | null;
  organizations: UserOrganizationMembership[];
  isLoading: boolean;
  activeOrgId: string | null;
  isPersonalMode: boolean;
  switchOrganization: (orgId: string | null) => void;
  switchToPersonal: () => void;
  // Navigation context helpers
  getNavigationContext: () => 'personal' | 'org-student' | 'org-instructor';
  getUserRole: () => 'owner' | 'instructor' | 'student' | null;
}

const OrgContext = createContext<OrgContextType | undefined>(undefined);

export function OrgProvider({ children, orgId }: { children: React.ReactNode; orgId?: string }) {
  const { user } = useAuth();
  const { data: organizations = [], isLoading } = useUserOrganizations();
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();
  const [activeOrgId, setActiveOrgId] = useState<string | null>(null);
  const [currentOrg, setCurrentOrg] = useState<UserOrganizationMembership | null>(null);

  // Initialize activeOrgId from URL params, props, or localStorage (safe)
  useEffect(() => {
    const orgFromUrl = searchParams.get('org');
    const orgFromStorage = safeLocalStorage.getItem<string>('fpk.activeOrgId', {
      fallbackValue: null,
      logErrors: false
    });
    
    // Priority: URL param > orgId prop > localStorage
    if (orgFromUrl) {
      setActiveOrgId(orgFromUrl);
      safeLocalStorage.setItem('fpk.activeOrgId', orgFromUrl);
    } else if (orgId) {
      setActiveOrgId(orgId);
      safeLocalStorage.setItem('fpk.activeOrgId', orgId);
      // Update URL to include org parameter for consistency
      if (!searchParams.get('org')) {
        const newSearchParams = new URLSearchParams(searchParams);
        newSearchParams.set('org', orgId);
        setSearchParams(newSearchParams, { replace: true });
      }
    } else if (orgFromStorage && orgFromStorage !== 'null') {
      setActiveOrgId(orgFromStorage);
    } else {
      setActiveOrgId(null);
    }
  }, [searchParams, orgId]);

  // Update currentOrg when activeOrgId or organizations change
  useEffect(() => {
    if (activeOrgId && organizations.length > 0) {
      const org = organizations.find(o => o.organization_id === activeOrgId);
      setCurrentOrg(org || null);
    } else {
      setCurrentOrg(null);
    }
  }, [activeOrgId, organizations]);

  const switchOrganization = (orgId: string | null) => {
    setActiveOrgId(orgId);
    
    if (orgId) {
      safeLocalStorage.setItem('fpk.activeOrgId', orgId);
      
      // Find the organization to check the user's role
      const selectedOrg = organizations.find(o => o.organization_id === orgId);
      
      // Navigate to appropriate dashboard based on role and current location
      if (window.location.pathname.includes('/dashboard/')) {
        if (selectedOrg?.role === 'owner' || selectedOrg?.role === 'instructor') {
          // Navigate to instructor dashboard with org parameter
          navigate(`/dashboard/instructor?org=${orgId}`);
        } else {
          // For students, stay on learner dashboard but add org parameter
          const newSearchParams = new URLSearchParams(searchParams);
          newSearchParams.set('org', orgId);
          setSearchParams(newSearchParams);
        }
      }
    } else {
      safeLocalStorage.removeItem('fpk.activeOrgId');
      // Remove org param from URL
      const newSearchParams = new URLSearchParams(searchParams);
      newSearchParams.delete('org');
      setSearchParams(newSearchParams);
    }
  };

  const switchToPersonal = () => {
    switchOrganization(null);
    // Navigate to learner dashboard if in org context
    if (window.location.pathname.includes('/dashboard/instructor')) {
      navigate('/dashboard/learner');
    }
  };

  const isPersonalMode = activeOrgId === null;

  // Helper functions for navigation context
  const getNavigationContext = (): 'personal' | 'org-student' | 'org-instructor' => {
    if (isPersonalMode) {
      return 'personal';
    }
    
    if (currentOrg?.role === 'owner' || currentOrg?.role === 'instructor') {
      return 'org-instructor';
    }
    
    return 'org-student';
  };

  const getUserRole = (): 'owner' | 'instructor' | 'student' | null => {
    return currentOrg?.role || null;
  };

  return (
    <OrgContext.Provider value={{
      currentOrg,
      organizations,
      isLoading,
      activeOrgId,
      isPersonalMode,
      switchOrganization,
      switchToPersonal,
      getNavigationContext,
      getUserRole,
    }}>
      {children}
    </OrgContext.Provider>
  );
}

export function useOrgContext() {
  const context = useContext(OrgContext);
  if (context === undefined) {
    throw new Error('useOrgContext must be used within an OrgProvider');
  }
  return context;
}

import React, { useEffect } from 'react';
import { useOrgContext } from '@/components/organizations/OrgContext';
import { useOrgBranding } from '@/hooks/useOrgBranding';

interface OrgThemeProviderProps {
  children: React.ReactNode;
}

export function OrgThemeProvider({ children }: OrgThemeProviderProps) {
  const { currentOrg, isPersonalMode } = useOrgContext();
  const { data: branding } = useOrgBranding(currentOrg?.organization_id || null);

  useEffect(() => {
    const root = document.documentElement;

    if (isPersonalMode || !branding?.theme_accent) {
      // Reset to default theme
      root.style.removeProperty('--accent');
      root.style.removeProperty('--accent-foreground');
      return;
    }

    // Apply organization theme
    const accent = branding.theme_accent;
    
    // Convert hex to HSL if needed, or use CSS variable format
    if (accent.startsWith('#')) {
      const hsl = hexToHsl(accent);
      root.style.setProperty('--accent', hsl);
      // Calculate contrasting foreground color
      const foreground = isLightColor(accent) ? '0 0% 10%' : '0 0% 98%';
      root.style.setProperty('--accent-foreground', foreground);
    } else if (accent.includes('hsl') || accent.includes('%')) {
      // Already in HSL format
      root.style.setProperty('--accent', accent);
    } else {
      // Assume it's a CSS variable format like "280 100% 70%"
      root.style.setProperty('--accent', accent);
    }

    // Cleanup on unmount
    return () => {
      root.style.removeProperty('--accent');
      root.style.removeProperty('--accent-foreground');
    };
  }, [branding?.theme_accent, isPersonalMode]);

  return <>{children}</>;
}

// Utility function to convert hex to HSL
function hexToHsl(hex: string): string {
  const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  if (!result) return '280 100% 70%'; // fallback
  
  const r = parseInt(result[1], 16) / 255;
  const g = parseInt(result[2], 16) / 255;
  const b = parseInt(result[3], 16) / 255;
  
  const max = Math.max(r, g, b);
  const min = Math.min(r, g, b);
  let h, s, l = (max + min) / 2;
  
  if (max === min) {
    h = s = 0; // achromatic
  } else {
    const d = max - min;
    s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
    switch (max) {
      case r: h = (g - b) / d + (g < b ? 6 : 0); break;
      case g: h = (b - r) / d + 2; break;
      case b: h = (r - g) / d + 4; break;
      default: h = 0;
    }
    h /= 6;
  }
  
  return `${Math.round(h * 360)} ${Math.round(s * 100)}% ${Math.round(l * 100)}%`;
}

// Utility to determine if a color is light (for contrast calculation)
function isLightColor(hex: string): boolean {
  const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  if (!result) return false;
  
  const r = parseInt(result[1], 16);
  const g = parseInt(result[2], 16);
  const b = parseInt(result[3], 16);
  
  // Using relative luminance formula
  const luminance = (0.299 * r + 0.587 * g + 0.114 * b) / 255;
  return luminance > 0.5;
}

