import React, { createContext, useContext, useEffect, useState, useRef } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { useGamification } from '@/hooks/useGamification';

interface GamificationContextType {
  userStats: any;
  refreshStats: () => Promise<void>;
  isLoading: boolean;
}

const GamificationContext = createContext<GamificationContextType | undefined>(undefined);

export const useGamificationContext = () => {
  const context = useContext(GamificationContext);
  if (!context) {
    throw new Error('useGamificationContext must be used within a GamificationProvider');
  }
  return context;
};

// Global channel tracking to prevent multiple subscriptions
const activeChannels = new Map<string, any>();

export const GamificationProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { user } = useAuth();
  const { userStats, fetchUserStats, isLoading } = useGamification();
  const [lastUpdate, setLastUpdate] = useState<number>(Date.now());
  const initializationRef = useRef<boolean>(false);

  const refreshStats = async () => {
    await fetchUserStats();
    setLastUpdate(Date.now());
  };

  // Cleanup function
  const cleanup = (userId: string) => {
    const channelKey = `gamification-${userId}`;
    const existingChannel = activeChannels.get(channelKey);
    
    if (existingChannel) {
      try {
        console.log('ðŸ§¹ Cleaning up existing gamification channel for user:', userId);
        supabase.removeChannel(existingChannel);
        activeChannels.delete(channelKey);
      } catch (error) {
        console.log('Error removing channel:', error);
      }
    }
  };

  useEffect(() => {
    // Prevent multiple initializations
    if (!user?.id || initializationRef.current) {
      return;
    }

    console.log('ðŸ”— Setting up gamification real-time subscription for user:', user.id);
    
    const channelKey = `gamification-${user.id}`;
    
    // Clean up any existing channel for this user
    cleanup(user.id);
    
    // Mark as initialized
    initializationRef.current = true;

    // Create a single channel with a stable name
    const channel = supabase.channel(channelKey, {
      config: {
        broadcast: { self: false }
      }
    });

    // Configure all the real-time listeners
    channel
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'user_xp',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          console.log('Real-time XP update detected');
          refreshStats();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'user_badges',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          console.log('Real-time badge update detected');
          refreshStats();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'achievements',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          console.log('Real-time achievement update detected');
          refreshStats();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'streaks',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          console.log('Real-time streak update detected');
          refreshStats();
        }
      );

    // Subscribe to the channel only once
    channel.subscribe((status) => {
      console.log('Gamification channel subscription status:', status);
      if (status === 'SUBSCRIBED') {
        console.log('âœ… Gamification channel successfully subscribed');
      } else if (status === 'CLOSED' || status === 'CHANNEL_ERROR') {
        console.log('âŒ Gamification channel closed or error');
        initializationRef.current = false;
      }
    });

    // Store the channel reference for cleanup
    activeChannels.set(channelKey, channel);

    // Cleanup function for effect
    return () => {
      initializationRef.current = false;
    };
  }, [user?.id]); // Only depend on user.id

  // Cleanup when user logs out
  useEffect(() => {
    if (!user?.id && initializationRef.current) {
      // Clean up all channels when user logs out
      activeChannels.forEach((channel, key) => {
        try {
          supabase.removeChannel(channel);
        } catch (error) {
          console.log('Error removing channel during logout:', error);
        }
      });
      activeChannels.clear();
      initializationRef.current = false;
    }
  }, [user?.id]);

  // Auto-refresh on mount
  useEffect(() => {
    if (user) {
      fetchUserStats();
    }
  }, [user, fetchUserStats]);

  const value = {
    userStats,
    refreshStats,
    isLoading
  };

  return (
    <GamificationContext.Provider value={value}>
      {children}
    </GamificationContext.Provider>
  );
};

import React, { createContext, useContext, useState, useEffect } from 'react';
import { safeLocalStorage } from '@/utils/safeStorage';
import { useCleanup } from '@/utils/cleanupManager';

interface VoiceSettings {
  enabled: boolean;
  autoRead: boolean;
  selectedVoice: string | null; // Browser voice name
  rate: number;
  pitch: number;
  volume: number;
  hasInteracted: boolean;
  paused: boolean;
}

interface BrowserVoice {
  id: string;
  name: string;
  gender: string;
}

interface VoiceSettingsContextType {
  settings: VoiceSettings;
  updateSettings: (updates: Partial<VoiceSettings>) => void;
  availableVoices: BrowserVoice[];
  isSupported: boolean;
  toggle: () => void;
  togglePaused: () => void;
  initializeVoice: () => Promise<void>;
  setSelectedVoice: (voiceId: string) => void;
  saveSettingsToStorage: () => void;
  loadSettingsFromStorage: () => Partial<VoiceSettings>;
}

const VoiceSettingsContext = createContext<VoiceSettingsContextType | undefined>(undefined);

export const useVoiceSettings = () => {
  const context = useContext(VoiceSettingsContext);
  if (!context) {
    throw new Error('useVoiceSettings must be used within a VoiceSettingsProvider');
  }
  return context;
};

// Get browser voices and map them to our interface
const getBrowserVoices = (): BrowserVoice[] => {
  if (!('speechSynthesis' in window)) return [];
  
  const voices = window.speechSynthesis.getVoices();
  return voices.map(voice => ({
    id: voice.name,
    name: `${voice.name} (${voice.lang})`,
    gender: voice.name.toLowerCase().includes('female') || voice.name.toLowerCase().includes('woman') ? 'female' : 'male'
  }));
};

export const VoiceSettingsProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const cleanup = useCleanup('VoiceSettingsProvider');
  const [settings, setSettings] = useState<VoiceSettings>({
    enabled: true,
    autoRead: true,
    selectedVoice: null, // Will be set to first available voice
    rate: 1.0,
    pitch: 1.0,
    volume: 0.8,
    hasInteracted: false,
    paused: false
  });
  
  const [availableVoices, setAvailableVoices] = useState<BrowserVoice[]>([]);
  const isSupported = 'speechSynthesis' in window;

  // Load voices when available
  useEffect(() => {
    if (!isSupported) return;

    const loadVoices = () => {
      const voices = getBrowserVoices();
      setAvailableVoices(voices);
      
      // Set default voice if none selected
      if (!settings.selectedVoice && voices.length > 0) {
        const defaultVoice = voices.find(v => v.name.includes('English')) || voices[0];
        setSettings(prev => ({ ...prev, selectedVoice: defaultVoice.id }));
      }
    };

    // Load voices immediately and also when they change
    loadVoices();
    window.speechSynthesis.onvoiceschanged = loadVoices;

    return () => {
      window.speechSynthesis.onvoiceschanged = null;
    };
  }, [isSupported, settings.selectedVoice]);

  // Load settings from localStorage on mount
  const loadSettingsFromStorage = (): Partial<VoiceSettings> => {
    const stored = safeLocalStorage.getItem<Partial<VoiceSettings>>('voiceSettings', {
      fallbackValue: {},
      logErrors: false
    });
    
    if (stored && typeof stored === 'object') {
      console.log('ðŸ”Š Loaded voice settings from storage:', stored);
      return stored;
    }
    
    return {};
  };

  // Save settings to localStorage (safe)
  const saveSettingsToStorage = () => {
    const success = safeLocalStorage.setItem('voiceSettings', {
      enabled: settings.enabled,
      autoRead: settings.autoRead,
      selectedVoice: settings.selectedVoice,
      rate: settings.rate,
      pitch: settings.pitch,
      volume: settings.volume
    });
    
    if (success) {
      console.log('ðŸ”Š Saved voice settings to storage');
    }
  };

  // Load settings on mount
  useEffect(() => {
    const storedSettings = loadSettingsFromStorage();
    if (Object.keys(storedSettings).length > 0) {
      // Always ensure TTS is enabled by default, even if stored settings say otherwise
      setSettings(prev => ({ ...prev, ...storedSettings, enabled: true }));
    }
  }, []);

  // Save settings whenever they change - debounced to prevent performance issues
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      saveSettingsToStorage();
    }, 1000); // Debounce saves by 1 second

    return () => clearTimeout(timeoutId);
  }, [settings.enabled, settings.autoRead, settings.selectedVoice, settings.rate, settings.pitch, settings.volume]);

  // Track user interaction for TTS
  useEffect(() => {
    const handleUserInteraction = () => {
      if (!settings.hasInteracted) {
        console.log('ðŸ”Š User interaction detected, enabling browser TTS');
        setSettings(prev => ({ ...prev, hasInteracted: true }));
      }
    };

    cleanup.addEventListener(document, 'click', handleUserInteraction);
    cleanup.addEventListener(document, 'keydown', handleUserInteraction);
  }, [settings.hasInteracted, cleanup]);

  const initializeVoice = async (): Promise<void> => {
    console.log('ðŸ”Š Browser voice system initialized with', availableVoices.length, 'voices');
  };

  const updateSettings = (updates: Partial<VoiceSettings>) => {
    setSettings(prev => ({ ...prev, ...updates }));
  };

  const setSelectedVoice = (voiceId: string) => {
    console.log('ðŸ”Š Setting selected voice to:', voiceId);
    setSettings(prev => ({ ...prev, selectedVoice: voiceId }));
  };

  const toggle = () => {
    const newEnabled = !settings.enabled;
    console.log('ðŸ”Š Browser TTS toggled:', newEnabled ? 'enabled' : 'disabled');
    setSettings(prev => ({ ...prev, enabled: newEnabled, paused: false }));
  };

  const togglePaused = () => {
    const newPaused = !settings.paused;
    console.log('ðŸ”Š Pause state toggled to:', newPaused);
    setSettings(prev => ({ ...prev, paused: newPaused }));
  };

  const value = {
    settings,
    updateSettings,
    availableVoices,
    isSupported,
    toggle,
    togglePaused,
    initializeVoice,
    setSelectedVoice,
    saveSettingsToStorage,
    loadSettingsFromStorage
  };

  return (
    <VoiceSettingsContext.Provider value={value}>
      {children}
    </VoiceSettingsContext.Provider>
  );
};

import React, { useState, useEffect, createContext, useContext } from 'react';
import { supabase } from '@/integrations/supabase/client';
import type { User, Session } from '@supabase/supabase-js';
import { logger } from '@/utils/logger';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signOut: () => Promise<void>;
}

interface AuthProviderProps {
  children: React.ReactNode;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log('useAuth: Setting up authentication listeners');
    console.log('useAuth: Current domain:', window.location.hostname);
    
    // Set up auth state listener
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (event, session) => {
        console.log('useAuth: Auth state changed', { event, hasSession: !!session, hasUser: !!session?.user });
        logger.auth('Auth state changed', { event, hasSession: !!session, hasUser: !!session?.user });
        setSession(session);
        setUser(session?.user ?? null);
        setLoading(false);
      }
    );

    // Get initial session
    console.log('useAuth: Getting initial session...');
    supabase.auth.getSession().then(({ data: { session }, error }) => {
      console.log('useAuth: Initial session result', { hasSession: !!session, error });
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    }).catch((err) => {
      console.error('useAuth: Error getting initial session:', err);
      setLoading(false); // Still set loading to false even on error
    });

    return () => {
      console.log('useAuth: Cleaning up auth listeners');
      subscription.unsubscribe();
    };
  }, []);

  const signOut = async () => {
    await supabase.auth.signOut();
  };

  return (
    <AuthContext.Provider value={{ user, session, loading, signOut }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

