import { useCallback } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { useXPIntegration } from '@/hooks/useXPIntegration';
import { useToast } from '@/hooks/use-toast';

interface ChallengeStartEvent {
  challengeType: 'quick_review' | 'accuracy_challenge' | 'speed_test' | 'custom_practice';
  mode: 'random' | 'custom';
  cardCount: number;
  timestamp: string;
}

interface ChallengeCompleteEvent extends ChallengeStartEvent {
  correctCount: number;
  timeTakenSec: number;
}

interface XPAwardedEvent {
  challengeType: string;
  xpAmount: number;
  reason: string;
  timestamp: string;
}

export function useChallengeAnalytics() {
  const { user } = useAuth();
  const { awardChallengeCompletionXP } = useXPIntegration();
  const { toast } = useToast();

  const trackChallengeStart = useCallback(async (
    challengeType: ChallengeStartEvent['challengeType'],
    mode: 'random' | 'custom',
    cardCount: number
  ) => {
    if (!user?.id) return;

    const event: ChallengeStartEvent = {
      challengeType,
      mode,
      cardCount,
      timestamp: new Date().toISOString()
    };

    console.log('ðŸ“Š Challenge Started:', event);

    // Feature implementation: Send to analytics pipeline (Supabase edge function)
    // For now, we'll log the event and could extend this to send to our analytics system
    try {
      // This could be extended to send to a dedicated analytics edge function
      console.log('Analytics Event - challenge_start:', event);
    } catch (error) {
      console.error('Failed to track challenge start:', error);
    }
  }, [user?.id]);

  const trackChallengeComplete = useCallback(async (
    challengeType: ChallengeCompleteEvent['challengeType'],
    mode: 'random' | 'custom',
    cardCount: number,
    correctCount: number,
    timeTakenSec: number,
    startTime?: number
  ) => {
    if (!user?.id) return;

    const actualTimeTaken = startTime ? Math.floor((Date.now() - startTime) / 1000) : timeTakenSec;

    const event: ChallengeCompleteEvent = {
      challengeType,
      mode,
      cardCount,
      correctCount,
      timeTakenSec: actualTimeTaken,
      timestamp: new Date().toISOString()
    };

    console.log('ðŸ“Š Challenge Completed:', event);

    // Calculate XP based on challenge type and performance
    const xpAmount = calculateChallengeXP(challengeType, cardCount, correctCount, actualTimeTaken);
    
    try {
      // Award XP through existing system
      await awardChallengeCompletionXP(challengeType, xpAmount);

      // Track XP awarded event
      const xpEvent: XPAwardedEvent = {
        challengeType,
        xpAmount,
        reason: `Completed ${challengeType.replace('_', ' ')} challenge`,
        timestamp: new Date().toISOString()
      };

      console.log('ðŸ“Š XP Awarded:', xpEvent);

      // Show success toast to user
      toast({
        title: `ðŸŽ‰ Challenge Complete!`,
        description: `You earned ${xpAmount} XP for completing the ${challengeType.replace('_', ' ')} challenge!`,
        duration: 4000,
      });

      // Track completion event
      console.log('Analytics Event - challenge_complete:', event);
      console.log('Analytics Event - xp_awarded:', xpEvent);

    } catch (error) {
      console.error('Failed to award XP for challenge completion:', error);
      toast({
        title: "Challenge Complete",
        description: "Challenge completed successfully!",
        duration: 3000,
      });
    }
  }, [user?.id, awardChallengeCompletionXP]);

  return {
    trackChallengeStart,
    trackChallengeComplete
  };
}

function calculateChallengeXP(
  challengeType: string,
  cardCount: number,
  correctCount: number,
  timeTakenSec: number
): number {
  switch (challengeType) {
    case 'quick_review':
      // +10 XP per card reviewed
      return cardCount * 10;
      
    case 'accuracy_challenge':
      // +20 XP if â‰¥80% correct, else +5 XP per correct
      const accuracy = cardCount > 0 ? (correctCount / cardCount) * 100 : 0;
      if (accuracy >= 80) {
        return 20 * cardCount;
      } else {
        return Math.max(correctCount * 5, 10); // Minimum 10 XP for attempting
      }
      
    case 'speed_test':
      // +15 XP per card answered within time limit, bonus +50 XP for 100%
      let speedXP = correctCount * 15;
      if (correctCount === cardCount && cardCount > 0) {
        speedXP += 50; // Perfect score bonus
      }
      return Math.max(speedXP, 10);
      
    case 'custom_practice':
      // +10 XP per card, +10 XP bonus for custom selection effort
      return (cardCount * 10) + 10;
      
    default:
      return Math.max(cardCount * 5, 10); // Fallback
  }
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { useConversationState } from '@/hooks/useConversationState';

interface ChatMessage {
  id: string;
  session_id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
}

export const useChatMessages = (sessionId: string | null) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();
  const { analyzeConversation } = useConversationState();

  // Load messages for a session
  const loadMessages = async () => {
    // Critical validation: ensure we have valid UUIDs before database queries
    if (!sessionId || !user?.id || typeof sessionId !== 'string' || typeof user.id !== 'string') {
      console.warn('Invalid parameters for loadMessages:', { sessionId, userId: user?.id });
      setMessages([]);
      return;
    }

    setIsLoading(true);
    try {
      const { data, error } = await supabase
        .from('chat_messages')
        .select('*')
        .eq('session_id', sessionId)
        .order('timestamp', { ascending: true });

      if (error) throw error;
      
      // Type-safe conversion with proper role casting
      const typedMessages: ChatMessage[] = (data || []).map(msg => ({
        id: msg.id,
        session_id: msg.session_id,
        role: msg.role as 'user' | 'assistant',
        content: msg.content,
        timestamp: msg.timestamp
      }));
      
      setMessages(typedMessages);
    } catch (error) {
      console.error('Error loading messages:', error);
      toast({
        title: "Error loading messages",
        description: "There was a problem loading the chat messages.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Add a message to the current session
  const addMessage = async (content: string, role: 'user' | 'assistant') => {
    // Critical validation: ensure we have valid UUIDs before database operations
    if (!sessionId || !user?.id || typeof sessionId !== 'string' || typeof user.id !== 'string') {
      console.warn('Invalid parameters for addMessage:', { sessionId, userId: user?.id });
      return null;
    }

    try {
      const { data, error } = await supabase
        .from('chat_messages')
        .insert({
          session_id: sessionId,
          role,
          content,
          timestamp: new Date().toISOString()
        })
        .select()
        .single();

      if (error) throw error;

      // Type-safe message addition
      const newMessage: ChatMessage = {
        id: data.id,
        session_id: data.session_id,
        role: data.role as 'user' | 'assistant',
        content: data.content,
        timestamp: data.timestamp
      };

      setMessages(prev => [...prev, newMessage]);
      return newMessage;
    } catch (error) {
      console.error('Error adding message:', error);
      toast({
        title: "Error sending message",
        description: "There was a problem sending your message.",
        variant: "destructive"
      });
      return null;
    }
  };

  // Simplified hybrid sendMessage with backend-driven intelligence
  const sendMessage = async (content: string, context?: string, chatMode: 'personal' | 'general' = 'general') => {
    if (!user?.id || isSending || typeof user.id !== 'string') {
      throw new Error('Invalid user state');
    }
    
    if (!sessionId || typeof sessionId !== 'string') {
      throw new Error('No active session. Please refresh the page.');
    }

    setIsSending(true);
    
    try {
      // Add user message first
      const userMessage = await addMessage(content, 'user');
      if (!userMessage) {
        throw new Error('Failed to save user message');
      }

      // Backend conversation analysis - moved from AI layer
      const conversationHistory = messages.map(msg => ({
        role: msg.role,
        content: msg.content,
        timestamp: msg.timestamp
      }));

      let conversationState;
      try {
        conversationState = analyzeConversation(conversationHistory, content);
      } catch (error) {
        // Fallback to default state
        conversationState = {
          promptType: 'general_guidance',
          isInQuiz: false,
          inRefresherMode: false,
          lastAIQuestion: null,
          currentTopic: null,
          incorrectAnswersCount: 0,
          teachingMethods: []
        };
      }
      
      // Prepare simple context data for Edge Function
      const contextData: Record<string, unknown> = {};
      
      if (conversationState.promptType === 'initiate_quiz' && conversationState.currentTopic) {
        contextData.quizTopic = conversationState.currentTopic;
      }
      
      if (conversationState.promptType === 'evaluate_answer') {
        contextData.teachingHistory = conversationState.teachingMethods?.join(', ') || '';
        contextData.incorrectCount = conversationState.incorrectAnswersCount;
      }

      // Call simplified AI function with backend-determined prompt type
      const { data, error } = await supabase.functions.invoke('ai-study-chat', {
        body: { 
          message: content,
          userId: user.id,
          sessionId: sessionId,
          promptType: conversationState.promptType,
          contextData: contextData,
          chatMode: chatMode,
          voiceActive: false
        }
      });

      if (error) {
        console.error('ðŸš¨ Hybrid AI function error:', error);
        throw error;
      }

      console.log('âœ… Hybrid response received:', {
        promptType: conversationState.promptType,
        hasResponse: !!data?.response,
        source: data?.source,
        error: data?.error
      });

      // Better error handling and contextual responses
      if (!data?.response) {
        console.error('ðŸš¨ No response from AI function:', data);
        
        // Show specific error information to help diagnose the issue
        if (data?.error) {
          console.error('ðŸš¨ Edge Function Error Details:', data.error);
          
          // If it's an API authentication error, show helpful message
          if (data.error.includes('401') || data.error.includes('authentication_error')) {
            const errorMsg = 'The AI service is experiencing authentication issues. The response has been generated using fallback methods.';
            console.log('ðŸ”„ Using fallback response due to API auth issue');
            
            // Use the fallback response from the Edge Function
            const fallbackResponse = data?.response || getContextualErrorResponse(content, chatMode, new Error('API authentication issue'));
            await addMessage(fallbackResponse, 'assistant');
            return;
          }
        }
        
        throw new Error(`AI service error: ${data?.error || 'No response received'}`);
      }

      const aiResponse = data.response;
      
      // Add AI response
      const assistantMessage = await addMessage(aiResponse, 'assistant');
      if (!assistantMessage) {
        throw new Error('Failed to save AI response');
      }

      // Update session metadata on first message
      if (messages.length <= 1) {
        const title = content.length > 40 ? content.substring(0, 40) + '...' : content;
        const contextTag = context?.includes('Notes') ? 'Study Notes' : 
                         context?.includes('Coach') ? 'AI Coaching' : 
                         context?.includes('Flashcard') ? 'Flashcard Help' : 
                         content.toLowerCase().includes('quiz') ? 'Quiz Help' :
                         chatMode === 'personal' ? 'Socratic Learning Session' : 'General Knowledge Exploration';
                         
        await supabase
          .from('chat_sessions')
          .update({ 
            title, 
            updated_at: new Date().toISOString(),
            context_tag: contextTag
          })
          .eq('id', sessionId);
      }

      toast({
        title: "ðŸŽ¯ Hybrid AI Active",
        description: `Using ${conversationState.promptType.replace('_', ' ')} approach`,
      });

    } catch (error) {
      console.error('âŒ Error in hybrid sendMessage:', error);
      
      // Provide a contextual error response that acknowledges what the user asked
      const contextualFallback = getContextualErrorResponse(content, chatMode, error);
      
      await addMessage(contextualFallback, 'assistant');
      
      toast({
        title: "âš ï¸ Connection Issue",
        description: `There's a technical problem, but I tried to help with "${content.substring(0, 30)}..."`,
        variant: "destructive"
      });
    } finally {
      setIsSending(false);
    }
  };

  useEffect(() => {
    loadMessages();
  }, [sessionId]);

  return {
    messages,
    isLoading,
    isSending,
    sendMessage,
    addMessage,
    loadMessages
  };
};

// Helper function to provide contextual error responses
function getContextualErrorResponse(message: string, chatMode: string, error: Error): string {
  const lowerMessage = message.toLowerCase();
  
  // Math questions - provide the answer even if AI is down
  if (lowerMessage.match(/\d+\s*[\+\-\*\/]\s*\d+/)) {
    const mathMatch = message.match(/(\d+)\s*([\+\-\*\/])\s*(\d+)/);
    if (mathMatch) {
      const [, num1, op, num2] = mathMatch;
      const a = parseInt(num1), b = parseInt(num2);
      let result;
      switch(op) {
        case '+': result = a + b; break;
        case '-': result = a - b; break;
        case '*': result = a * b; break;
        case '/': result = b !== 0 ? a / b : 'undefined (cannot divide by zero)'; break;
      }
      
      return `I can still help with your math question! ${num1} ${op} ${num2} = ${result} ðŸ§®\n\nI'm having technical difficulties with my full AI capabilities, but I wanted to make sure you got an answer to your calculation. Once I'm back online, I can guide you through more complex mathematical concepts!`;
    }
  }
  
  // Extract key topics from the message
  const topics = [];
  if (lowerMessage.includes('cloud')) topics.push('clouds');
  if (lowerMessage.includes('weather')) topics.push('weather');
  if (lowerMessage.includes('science')) topics.push('science');
  if (lowerMessage.includes('math')) topics.push('mathematics');
  if (lowerMessage.includes('history')) topics.push('history');
  
  const topicText = topics.length > 0 ? topics.join(', ') : 'this topic';
  
  if (chatMode === 'personal') {
    return `I apologize - I'm experiencing technical difficulties and can't provide my full Socratic guidance right now. But I noticed you're interested in ${topicText}! 

While I work on reconnecting, here's what I'd normally ask you: What do you already know about ${topicText}? What specific aspect makes you curious? 

Error details: ${error.message || 'Connection issue'}`;
  } else {
    return `I'm having technical difficulties but I see you're asking about ${topicText}. Let me try to help while I resolve the connection issue.

What specific aspect of ${topicText} would you like to explore? I'll do my best to assist even with limited capabilities.

Technical details: ${error.message || 'Service temporarily unavailable'}`;
  }
}

import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { safeLocalStorage } from '@/utils/safeStorage';
import { logger } from '@/utils/logger';

export interface ConsentPreferences {
  essential: boolean;
  analytics: boolean;
  marketing: boolean;
  functional: boolean;
}

export interface ConsentContextType {
  preferences: ConsentPreferences;
  hasConsent: (type: keyof ConsentPreferences) => boolean;
  updateConsent: (preferences: ConsentPreferences) => Promise<void>;
  withdrawConsent: (type: keyof ConsentPreferences) => Promise<void>;
  isLoading: boolean;
}

const ConsentContext = createContext<ConsentContextType | undefined>(undefined);

export function ConsentProvider({ children }: { children: ReactNode }) {
  const { user } = useAuth();
  const [preferences, setPreferences] = useState<ConsentPreferences>({
    essential: true,
    analytics: false,
    marketing: false,
    functional: false,
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadConsent();
  }, [user]);

  const loadConsent = async () => {
    setIsLoading(true);
    try {
      // First check localStorage for immediate access
      const localConsent = safeLocalStorage.getItem<string>('cookie-consent', {
        fallbackValue: null,
        logErrors: false
      });
      if (localConsent) {
        try {
          const parsed = typeof localConsent === 'string' ? JSON.parse(localConsent) : localConsent;
          setPreferences(parsed);
        } catch (parseError) {
          logger.warn('Failed to parse consent preferences, using defaults', 'CONSENT', { localConsent, error: parseError });
          // Reset corrupted data
          safeLocalStorage.removeItem('cookie-consent');
        }
      }

      // If user is authenticated, load from database
      if (user) {
        const { data, error } = await supabase
          .from('user_consent')
          .select('consent_type, is_granted, withdrawn_at')
          .eq('user_id', user.id)
          .is('withdrawn_at', null);

        if (error) {
          logger.error('Error loading consent', 'CONSENT', error);
          return;
        }

        // Convert database records to preferences object
        const dbPreferences: ConsentPreferences = {
          essential: true, // Always true
          analytics: false,
          marketing: false,
          functional: false,
        };

        data?.forEach((record) => {
          if (record.consent_type in dbPreferences) {
            dbPreferences[record.consent_type as keyof ConsentPreferences] = record.is_granted;
          }
        });

        setPreferences(dbPreferences);
        // Sync with localStorage
        safeLocalStorage.setItem('cookie-consent', JSON.stringify(dbPreferences));
      }
    } catch (error) {
      logger.error('Error loading consent preferences', 'CONSENT', error);
    } finally {
      setIsLoading(false);
    }
  };

  const updateConsent = async (newPreferences: ConsentPreferences) => {
    setPreferences(newPreferences);
    safeLocalStorage.setItem('cookie-consent', JSON.stringify(newPreferences));

    // If user is authenticated, save to database
    if (user) {
      try {
        const clientIP = await getClientIP();
        const consentRecords = Object.entries(newPreferences).map(([type, granted]) => ({
          user_id: user.id,
          consent_type: type,
          is_granted: granted,
          granted_at: granted ? new Date().toISOString() : null,
          legal_basis: 'consent',
          purpose: getConsentPurpose(type),
          ip_address: clientIP,
          user_agent: navigator.userAgent,
        }));

        // Insert new consent records
        const { error } = await supabase
          .from('user_consent')
          .insert(consentRecords);

        if (error) {
          logger.error('Error saving consent', 'CONSENT', error);
        }

        // Record audit event
        await recordAuditEvent('update', 'user_consent', null, null, {
          consent_preferences: newPreferences
        }, 'consent', 'User updated cookie consent preferences');

      } catch (error) {
        logger.error('Error updating consent in database', 'CONSENT', error);
      }
    }

    // Apply consent preferences immediately
    applyConsentPreferences(newPreferences);
  };

  const withdrawConsent = async (type: keyof ConsentPreferences) => {
    const updatedPreferences = { ...preferences, [type]: false };
    await updateConsent(updatedPreferences);

    // Mark specific consent as withdrawn in database
    if (user) {
      try {
        await supabase
          .from('user_consent')
          .update({ 
            withdrawn_at: new Date().toISOString(),
            is_granted: false 
          })
          .eq('user_id', user.id)
          .eq('consent_type', type)
          .is('withdrawn_at', null);

        await recordAuditEvent('withdraw', 'user_consent', null, null, {
          consent_type: type
        }, 'consent', `User withdrew ${type} consent`);

      } catch (error) {
        logger.error('Error withdrawing consent', 'CONSENT', error);
      }
    }
  };

  const hasConsent = (type: keyof ConsentPreferences): boolean => {
    return preferences[type];
  };

  const applyConsentPreferences = (prefs: ConsentPreferences) => {
    // Analytics consent
    if (!prefs.analytics) {
      // Disable analytics tracking
      window.gtag?.('consent', 'update', {
        analytics_storage: 'denied'
      });
      // Clear analytics cookies
      clearAnalyticsCookies();
    } else {
      window.gtag?.('consent', 'update', {
        analytics_storage: 'granted'
      });
    }

    // Marketing consent
    if (!prefs.marketing) {
      window.gtag?.('consent', 'update', {
        ad_storage: 'denied',
        ad_user_data: 'denied',
        ad_personalization: 'denied'
      });
      clearMarketingCookies();
    } else {
      window.gtag?.('consent', 'update', {
        ad_storage: 'granted',
        ad_user_data: 'granted',
        ad_personalization: 'granted'
      });
    }

    // Functional consent
    if (!prefs.functional) {
      // Clear functional cookies except essential ones
      clearFunctionalCookies();
    }
  };

  const value: ConsentContextType = {
    preferences,
    hasConsent,
    updateConsent,
    withdrawConsent,
    isLoading,
  };

  return (
    <ConsentContext.Provider value={value}>
      {children}
    </ConsentContext.Provider>
  );
}

export function useConsent() {
  const context = useContext(ConsentContext);
  if (context === undefined) {
    throw new Error('useConsent must be used within a ConsentProvider');
  }
  return context;
}

// Helper functions
function getConsentPurpose(type: string): string {
  const purposes = {
    essential: 'Required for platform functionality and security',
    analytics: 'Analyzing user behavior to improve platform performance',
    marketing: 'Personalizing content and delivering relevant advertisements',
    functional: 'Remembering user preferences and settings',
  };
  return purposes[type as keyof typeof purposes] || 'Unknown purpose';
}

async function getClientIP(): Promise<string | null> {
  try {
    const response = await fetch('https://api.ipify.org?format=json');
    const data = await response.json();
    return data.ip;
  } catch {
    return null;
  }
}

async function recordAuditEvent(
  action: string,
  tableName: string,
  recordId: string | null,
  oldValues: Record<string, unknown> | null,
  newValues: Record<string, unknown> | null,
  legalBasis: string,
  purpose: string
) {
  try {
    await supabase.rpc('record_audit_event', {
      p_user_id: (await supabase.auth.getUser()).data.user?.id,
      p_action: action,
      p_table_name: tableName,
      p_record_id: recordId,
        p_old_values: oldValues as any,
        p_new_values: newValues as any,
      p_legal_basis: legalBasis,
      p_purpose: purpose,
    });
  } catch (error) {
    logger.error('Error recording audit event', 'CONSENT', error);
  }
}

function clearAnalyticsCookies() {
  const analyticsCookies = ['_ga', '_ga_', '_gid', '_gat', '_gtag'];
  analyticsCookies.forEach(cookieName => {
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=.${window.location.hostname};`;
  });
}

function clearMarketingCookies() {
  const marketingCookies = ['_fbp', '_fbc', '__Secure-3PAPISID', '__Secure-3PSID'];
  marketingCookies.forEach(cookieName => {
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=.${window.location.hostname};`;
  });
}

function clearFunctionalCookies() {
  // Clear non-essential functional cookies
  const functionalCookies = ['theme', 'language', 'sidebar-state'];
  functionalCookies.forEach(cookieName => {
    document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
  });
}

// Extend Window interface for gtag
declare global {
  interface Window {
    gtag?: (...args: unknown[]) => void;
  }
}

// Conversation State Management Hook - Backend Intelligence Layer
import { useState, useCallback } from 'react';

export interface ConversationState {
  promptType: 'initiate_session' | 'initiate_quiz' | 'evaluate_answer' | 'evaluate_quiz_answer' | 'proactive_help' | 'evaluate_refresher' | 'direct_answer';
  isInQuiz: boolean;
  inRefresherMode: boolean;
  lastAIQuestion: string | null;
  currentTopic: string | null;
  incorrectAnswersCount: number;
  teachingMethods: string[];
}

export interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
}

export const useConversationState = () => {
  const [state, setState] = useState<ConversationState>({
    promptType: 'initiate_session',
    isInQuiz: false,
    inRefresherMode: false,
    lastAIQuestion: null,
    currentTopic: null,
    incorrectAnswersCount: 0,
    teachingMethods: []
  });

  // Analyze conversation history and determine next prompt type
  const analyzeConversation = useCallback((chatHistory: ChatMessage[], currentMessage: string): ConversationState => {
    const trimmed = currentMessage.trim();
    
    // Direct answer command
    if (trimmed.startsWith('/answer')) {
      return { ...state, promptType: 'direct_answer' };
    }

    // Struggle indicators
    if (isStruggleIndicator(trimmed)) {
      return { ...state, promptType: 'proactive_help' };
    }

    // Quiz request
    if (isQuizRequest(trimmed)) {
      return { 
        ...state, 
        promptType: 'initiate_quiz',
        isInQuiz: true,
        currentTopic: extractQuizTopic(trimmed)
      };
    }

    const lastAIMessage = chatHistory.length > 0 ? chatHistory[chatHistory.length - 1] : null;
    const lastAIQuestion = lastAIMessage?.role === 'assistant' ? extractLastQuestion(lastAIMessage.content) : null;
    
    // Check if user is answering a question
    if (lastAIQuestion && isLikelyAnswer(trimmed, lastAIQuestion)) {
      // In refresher mode
      if (state.inRefresherMode) {
        return { 
          ...state, 
          promptType: 'evaluate_refresher',
          lastAIQuestion 
        };
      }
      
      // In quiz mode
      if (state.isInQuiz || isInQuizSession(chatHistory)) {
        return { 
          ...state, 
          promptType: 'evaluate_quiz_answer',
          isInQuiz: true,
          lastAIQuestion 
        };
      }
      
      // Regular answer evaluation
      const teachingMethods = extractTeachingHistory(chatHistory);
      const incorrectCount = countRecentIncorrectAnswers(chatHistory);
      
      return { 
        ...state, 
        promptType: 'evaluate_answer',
        lastAIQuestion,
        teachingMethods,
        incorrectAnswersCount: incorrectCount
      };
    }

    // Default to initiate session for new questions
    return { 
      ...state, 
      promptType: 'initiate_session',
      lastAIQuestion 
    };
  }, [state]);

  // Helper functions moved from prompt-builder
  const isStruggleIndicator = (message: string): boolean => {
    const struggleKeywords = [
      /i need more help/i,
      /can you help/i,
      /i don't know/i,
      /i'm stuck/i,
      /i need to go back to the basics/i,
      /i'm confused/i,
      /this is hard/i,
      /i don't understand/i
    ];
    return struggleKeywords.some(pattern => pattern.test(message));
  };

  const isQuizRequest = (message: string): boolean => {
    const quizPatterns = [
      /quiz me on/i,
      /give me a quiz on/i,
      /test me on/i,
      /can you quiz me/i,
      /i want a quiz on/i,
      /quiz about/i,
      /test my knowledge/i
    ];
    return quizPatterns.some(pattern => pattern.test(message));
  };

  const extractQuizTopic = (message: string): string => {
    const patterns = [
      /(?:quiz me on|give me a quiz on|test me on|can you quiz me (?:on|about)|i want a quiz on|quiz about|test my knowledge (?:of|on|about))\s+(.+)/i
    ];
    
    for (const pattern of patterns) {
      const match = message.match(pattern);
      if (match && match[1]) {
        return match[1].trim().replace(/[?.!]*$/, '');
      }
    }
    return message.trim();
  };

  const extractLastQuestion = (content: string): string | null => {
    if (content.includes('?') || 
        content.toLowerCase().includes('what') ||
        content.toLowerCase().includes('how') ||
        content.toLowerCase().includes('why') ||
        content.toLowerCase().includes('when') ||
        content.toLowerCase().includes('where') ||
        content.toLowerCase().includes('tell me about') ||
        content.toLowerCase().includes('explain')) {
      return content;
    }
    return null;
  };

  const isLikelyAnswer = (message: string, lastQuestion: string): boolean => {
    const answerPatterns = [
      /^(yes|no|maybe|i think|i believe|is it|that's|this is|it's|the answer is)/i,
      /^(because|since|due to|as a result)/i,
      /^[a-zA-Z\s]{1,50}$/i,
    ];
    
    const numericAnswer = /^\s*[-+]?\d+(?:\.\d+)?\s*$/;
    const simpleMathExpr = /\b\d+\s*([x*Ã—+\/-])\s*\d+\b/;

    return answerPatterns.some(pattern => pattern.test(message.trim())) ||
           numericAnswer.test(message) ||
           simpleMathExpr.test(message) ||
           message.trim().length < 50;
  };

  const isInQuizSession = (chatHistory: ChatMessage[]): boolean => {
    for (let i = chatHistory.length - 1; i >= Math.max(0, chatHistory.length - 5); i--) {
      const msg = chatHistory[i];
      if (msg.role === 'assistant') {
        const content = msg.content.toLowerCase();
        if (content.includes('quiz') || content.includes('test') || 
            content.includes('let\'s start') || content.includes('great idea! let\'s begin')) {
          return true;
        }
      } else if (msg.role === 'user' && isQuizRequest(msg.content)) {
        return true;
      }
    }
    return false;
  };

  const extractTeachingHistory = (chatHistory: ChatMessage[]): string[] => {
    const teachingMethods: string[] = [];
    
    for (const msg of chatHistory) {
      if (msg.role === 'assistant') {
        const content = msg.content.toLowerCase();
        
        if (content.includes('add') && content.includes('times')) {
          teachingMethods.push('addition breakdown');
        }
        if (content.includes('analogy') || content.includes('like') || content.includes('imagine')) {
          teachingMethods.push('analogy method');
        }
        if (content.includes('break down') || content.includes('step by step')) {
          teachingMethods.push('step-by-step breakdown');
        }
        if (content.includes('example') || content.includes('for instance')) {
          teachingMethods.push('example illustration');
        }
        if (content.includes('visual') || content.includes('picture') || content.includes('draw')) {
          teachingMethods.push('visual approach');
        }
        if (content.includes('simpler') || content.includes('easier')) {
          teachingMethods.push('simplification method');
        }
        if (content.includes('pattern') || content.includes('sequence')) {
          teachingMethods.push('pattern recognition');
        }
        if (content.includes('group') || content.includes('set')) {
          teachingMethods.push('grouping method');
        }
      }
    }
    
    return [...new Set(teachingMethods)];
  };

  const countRecentIncorrectAnswers = (chatHistory: ChatMessage[]): number => {
    let count = 0;
    for (let i = chatHistory.length - 1; i >= 0; i--) {
      const msg = chatHistory[i];
      if (msg.role === 'assistant') {
        const content = msg.content.toLowerCase();
        if (content.includes('not quite') || content.includes('try again') || content.includes('incorrect')) {
          count++;
        } else if (content.includes('correct') || content.includes('exactly') || content.includes('that\'s it')) {
          break;
        }
      }
    }
    return count;
  };

  const updateState = (updates: Partial<ConversationState>) => {
    setState(prev => ({ ...prev, ...updates }));
  };

  return {
    state,
    analyzeConversation,
    updateState
  };
};

