import { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from './useAuth';
import { useSubscription } from './useSubscription';
import { supabase } from '@/integrations/supabase/client';

export type OnboardingStep = 
  | 'loading'
  | 'unauthenticated' 
  | 'choose-plan'
  | 'beta-onboarding'
  | 'dashboard';

interface OnboardingFlowState {
  currentStep: OnboardingStep;
  isLoading: boolean;
  error: string | null;
  hasCompletedBetaOnboarding: boolean;
}

export const useOnboardingFlow = () => {
  const { user, loading: authLoading, session } = useAuth();
  const { subscription, isLoading: subscriptionLoading } = useSubscription();
  const navigate = useNavigate();
  const location = useLocation();
  
  const [state, setState] = useState<OnboardingFlowState>({
    currentStep: 'loading',
    isLoading: true,
    error: null,
    hasCompletedBetaOnboarding: false
  });

  // Check if user has completed beta onboarding
  const checkBetaOnboardingStatus = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('profiles')
        .select('onboarding_completed')
        .eq('id', userId)
        .maybeSingle();

      if (error && error.code !== 'PGRST116') {
        console.error('Error checking onboarding status:', error);
        return false;
      }

      return data?.onboarding_completed || false;
    } catch (error) {
      console.error('Error checking onboarding status:', error);
      return false;
    }
  };

  // Determine current step based on user state
  const determineStep = async (): Promise<OnboardingStep> => {
    console.log('ðŸ” Determining onboarding step:', { 
      authLoading,
      subscriptionLoading,
      user: !!user, 
      subscription: subscription?.subscribed 
    });

    // Still loading auth state
    if (authLoading) {
      console.log('â³ Auth still loading...');
      return 'loading';
    }

    // Not authenticated
    if (!user || !session) {
      console.log('âž¡ï¸ No user/session - going to unauthenticated');
      return 'unauthenticated';
    }

    // Skip subscription check if it's taking too long and user exists
    if (subscriptionLoading) {
      console.log('â³ Subscription loading, but user exists - proceeding to dashboard');
      return 'dashboard'; // Fallback to dashboard if subscription is taking too long
    }

    // No subscription - need to choose plan
    if (!subscription?.subscribed) {
      console.log('âž¡ï¸ No subscription - going to choose-plan');
      return 'choose-plan';
    }

    // Has subscription, check beta onboarding
    console.log('ðŸ” Checking beta onboarding status...');
    try {
      const hasCompletedOnboarding = await checkBetaOnboardingStatus(user.id);
      
      if (!hasCompletedOnboarding) {
        console.log('âž¡ï¸ Beta onboarding incomplete - going to beta-onboarding');
        return 'beta-onboarding';
      }

      console.log('âœ… All checks passed - going to dashboard');
      return 'dashboard';
    } catch (error) {
      console.error('Error checking beta onboarding:', error);
      // Fallback to dashboard if beta check fails
      return 'dashboard';
    }
  };

  // Update flow state with proper dependency management
  useEffect(() => {
    let isMounted = true;
    let timeoutId: NodeJS.Timeout;
    
    const updateFlow = async () => {
      if (!isMounted) return;
      
      try {
        setState(prev => ({ ...prev, isLoading: true, error: null }));
        
        // Add timeout to prevent infinite loading
        timeoutId = setTimeout(() => {
          if (isMounted) {
            console.warn('âš ï¸ Onboarding flow timeout - forcing fallback navigation');
            const fallbackStep = user ? 'dashboard' : 'unauthenticated';
            setState(prev => ({
              ...prev,
              currentStep: fallbackStep,
              isLoading: false,
              error: 'Loading timeout - using fallback navigation'
            }));
          }
        }, 8000); // 8 second timeout
        
        const step = await determineStep();
        const hasCompletedOnboarding = user ? await checkBetaOnboardingStatus(user.id) : false;
        
        if (!isMounted) return;
        
        clearTimeout(timeoutId);
        setState(prev => ({
          ...prev,
          currentStep: step,
          isLoading: false,
          hasCompletedBetaOnboarding: hasCompletedOnboarding
        }));
        
        console.log('ðŸš€ Onboarding Flow Update:', {
          user: !!user,
          hasSubscription: subscription?.subscribed,
          hasCompletedOnboarding,
          currentStep: step,
          currentPath: location.pathname
        });
        
      } catch (error) {
        if (!isMounted) return;
        clearTimeout(timeoutId);
        console.error('Error determining onboarding step:', error);
        const fallbackStep = user ? 'dashboard' : 'unauthenticated';
        setState(prev => ({
          ...prev,
          currentStep: fallbackStep,
          isLoading: false,
          error: error instanceof Error ? error.message : 'Unknown error'
        }));
      }
    };

    // Only update if auth and subscription state are stable OR if we've been loading too long
    if (!authLoading && !subscriptionLoading) {
      updateFlow();
    } else {
      // Force update after 10 seconds even if still loading
      const forceUpdateTimeout = setTimeout(() => {
        if (isMounted) {
          console.warn('âš ï¸ Forcing onboarding update due to prolonged loading');
          updateFlow();
        }
      }, 10000);
      
      return () => {
        isMounted = false;
        clearTimeout(forceUpdateTimeout);
        if (timeoutId) clearTimeout(timeoutId);
      };
    }

    return () => {
      isMounted = false;
      if (timeoutId) clearTimeout(timeoutId);
    };
  }, [user?.id, authLoading, subscription?.subscribed, subscriptionLoading]); // Minimal stable dependencies

  // Navigation helpers
  const navigateToStep = (step: OnboardingStep) => {
    console.log('ðŸ”„ OnboardingFlow: Navigating to step:', step);
    
    // Prevent navigation during loading states
    if (state.isLoading) return;
    
    switch (step) {
      case 'unauthenticated':
        navigate('/login', { replace: true });
        break;
      case 'choose-plan':
        navigate('/choose-plan', { replace: true });
        break;
      case 'beta-onboarding':
        // Stay on current page but show onboarding
        break;
      case 'dashboard':
        // Try to restore previous route or go to default
        const currentPath = window.location.pathname;
        if (currentPath.startsWith('/dashboard')) {
          // Already on dashboard, don't navigate
          return;
        }
        navigate('/dashboard/learner', { replace: true });
        break;
    }
  };

  const completeOnboarding = async () => {
    if (!user) return false;
    
    try {
      const { error } = await supabase
        .from('profiles')
        .upsert({
          id: user.id,
          onboarding_completed: true,
          updated_at: new Date().toISOString()
        });

      if (error) {
        console.error('Error completing onboarding:', error);
        return false;
      }

      setState(prev => ({
        ...prev,
        hasCompletedBetaOnboarding: true,
        currentStep: 'dashboard'
      }));

      navigate('/dashboard/learner');
      return true;
    } catch (error) {
      console.error('Error completing onboarding:', error);
      return false;
    }
  };

  return {
    ...state,
    navigateToStep,
    completeOnboarding,
    shouldShowBetaOnboarding: state.currentStep === 'beta-onboarding'
  };
};

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { supabase } from '@/integrations/supabase/client';
import { toast } from 'sonner';
import { Course } from './useCourses';
import { NativeCourse } from './useNativeCourses';

export interface OrganizationCourseAssignment {
  id: string;
  organization_id: string;
  course_id?: string;
  native_course_id?: string;
  assigned_by: string;
  assigned_at: string;
  is_active: boolean;
  created_at: string;
}

export interface OrganizationCoursesResponse {
  globalCourses: Course[];
  nativeCourses: NativeCourse[];
  assignedCourses: Course[];
  assignedNativeCourses: NativeCourse[];
  organizationOwnedCourses: Course[];
  organizationOwnedNativeCourses: NativeCourse[];
}

// Hook to fetch courses available to an organization
export function useOrganizationCourses(organizationId: string) {
  return useQuery({
    queryKey: ['organization-courses', organizationId],
    queryFn: async (): Promise<OrganizationCoursesResponse> => {
      // Fetch global courses
      const { data: globalCourses, error: globalError } = await supabase
        .from('courses')
        .select('*')
        .eq('course_visibility', 'global')
        .eq('status', 'published');

      if (globalError) throw globalError;

      // Fetch global native courses
      const { data: globalNativeCourses, error: globalNativeError } = await supabase
        .from('native_courses')
        .select('*')
        .eq('visibility', 'published')
        .or('course_visibility.is.null,course_visibility.eq.global');

      if (globalNativeError) throw globalNativeError;

      // Fetch assigned courses
      const { data: assignments, error: assignmentsError } = await supabase
        .from('organization_course_assignments')
        .select(`
          *,
          courses (*),
          native_courses (*)
        `)
        .eq('organization_id', organizationId)
        .eq('is_active', true);

      if (assignmentsError) throw assignmentsError;

      // Fetch organization-owned courses
      const { data: orgCourses, error: orgCoursesError } = await supabase
        .from('courses')
        .select('*')
        .eq('organization_id', organizationId);

      if (orgCoursesError) throw orgCoursesError;

      const { data: orgNativeCourses, error: orgNativeCoursesError } = await supabase
        .from('native_courses')
        .select('*')
        .eq('organization_id', organizationId);

      if (orgNativeCoursesError) throw orgNativeCoursesError;

      const assignedCourses = assignments
        .filter(a => a.course_id && a.courses)
        .map(a => a.courses);

      const assignedNativeCourses = assignments
        .filter(a => a.native_course_id && a.native_courses)
        .map(a => a.native_courses);

      return {
        globalCourses: (globalCourses || []) as Course[],
        nativeCourses: (globalNativeCourses || []) as NativeCourse[],
        assignedCourses: (assignedCourses || []) as Course[],
        assignedNativeCourses: (assignedNativeCourses || []) as NativeCourse[],
        organizationOwnedCourses: (orgCourses || []) as Course[],
        organizationOwnedNativeCourses: (orgNativeCourses || []) as NativeCourse[],
      };
    },
    enabled: !!organizationId,
    staleTime: 1000 * 60 * 5, // 5 minutes
  });
}

// Hook to fetch available courses that can be assigned to an organization
export function useAvailableCourses(organizationId: string) {
  return useQuery({
    queryKey: ['available-courses', organizationId],
    queryFn: async () => {
      // Get already assigned course IDs
      const { data: assignments } = await supabase
        .from('organization_course_assignments')
        .select('course_id, native_course_id')
        .eq('organization_id', organizationId)
        .eq('is_active', true);

      const assignedCourseIds = assignments?.map(a => a.course_id).filter(Boolean) || [];
      const assignedNativeCourseIds = assignments?.map(a => a.native_course_id).filter(Boolean) || [];

      // Fetch global courses not already assigned
      const { data: availableCourses, error: coursesError } = await supabase
        .from('courses')
        .select('*')
        .eq('course_visibility', 'global')
        .eq('status', 'published')
        .not('id', 'in', `(${assignedCourseIds.join(',')})`);

      if (coursesError) throw coursesError;

      // Fetch global native courses not already assigned
      const { data: availableNativeCourses, error: nativeCoursesError } = await supabase
        .from('native_courses')
        .select('*')
        .eq('visibility', 'published')
        .or('course_visibility.is.null,course_visibility.eq.global')
        .not('id', 'in', `(${assignedNativeCourseIds.join(',')})`);

      if (nativeCoursesError) throw nativeCoursesError;

      return {
        courses: (availableCourses || []) as Course[],
        nativeCourses: (availableNativeCourses || []) as NativeCourse[],
      };
    },
    enabled: !!organizationId,
    staleTime: 1000 * 60 * 5,
  });
}

// Hook to manage course assignments
export function useOrganizationCourseAssignments() {
  const queryClient = useQueryClient();

  const assignCourse = useMutation({
    mutationFn: async ({
      organizationId,
      courseId,
      nativeCourseId,
    }: {
      organizationId: string;
      courseId?: string;
      nativeCourseId?: string;
    }) => {
      const { data, error } = await supabase
        .from('organization_course_assignments')
        .insert([{
          organization_id: organizationId,
          course_id: courseId,
          native_course_id: nativeCourseId,
          assigned_by: (await supabase.auth.getUser()).data.user?.id!,
        }])
        .select()
        .single();

      if (error) throw error;
      return data;
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['organization-courses', variables.organizationId] });
      queryClient.invalidateQueries({ queryKey: ['available-courses', variables.organizationId] });
      toast.success('Course assigned successfully!');
    },
    onError: (error) => {
      console.error('Assignment error:', error);
      toast.error('Failed to assign course');
    },
  });

  const unassignCourse = useMutation({
    mutationFn: async ({
      organizationId,
      courseId,
      nativeCourseId,
    }: {
      organizationId: string;
      courseId?: string;
      nativeCourseId?: string;
    }) => {
      let query = supabase
        .from('organization_course_assignments')
        .update({ is_active: false })
        .eq('organization_id', organizationId);

      if (courseId) {
        query = query.eq('course_id', courseId);
      } else if (nativeCourseId) {
        query = query.eq('native_course_id', nativeCourseId);
      }

      const { error } = await query;
      if (error) throw error;
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['organization-courses', variables.organizationId] });
      queryClient.invalidateQueries({ queryKey: ['available-courses', variables.organizationId] });
      toast.success('Course unassigned successfully!');
    },
    onError: (error) => {
      console.error('Unassignment error:', error);
      toast.error('Failed to unassign course');
    },
  });

  return {
    assignCourse,
    unassignCourse,
    isAssigning: assignCourse.isPending,
    isUnassigning: unassignCourse.isPending,
  };
}

import { useLocation } from 'react-router-dom';

export const usePageContext = () => {
  const location = useLocation();

  const getPageContext = () => {
    const path = location.pathname;
    
    // Map routes to user-friendly context
    const contextMap: Record<string, string> = {
      '/dashboard/learner': 'Dashboard Overview',
      '/dashboard/learner/notes': 'Notes & Study Materials',
      '/dashboard/learner/flashcards': 'Flashcard Management',
      '/dashboard/learner/study': 'Study Sessions',
      '/dashboard/learner/ai-coach': 'AI Learning Coach',
      '/dashboard/learner/goals': 'Learning Goals',
      '/dashboard/learner/analytics': 'Learning Analytics',
      '/dashboard/learner/settings': 'Settings',
      '/dashboard/learner/courses': 'Course Library'
    };

    // Extract course or module context if present
    const courseMatch = path.match(/\/courses\/([^\/]+)/);
    const moduleMatch = path.match(/\/modules\/([^\/]+)/);
    
    if (courseMatch) {
      return `Course: ${courseMatch[1].replace(/-/g, ' ')}`;
    }
    
    if (moduleMatch) {
      return `Module: ${moduleMatch[1].replace(/-/g, ' ')}`;
    }

    return contextMap[path] || 'FPK University Platform';
  };

  return { getPageContext };
};

import { useEffect, useRef } from 'react';
import { supabase } from '@/integrations/supabase/client';
import type { RealtimeChannel } from '@supabase/supabase-js';

interface RealtimeOptions {
  event?: 'INSERT' | 'UPDATE' | 'DELETE' | '*';
  schema?: string;
  table: string;
  filter?: string;
}

// Global registry to prevent duplicate subscriptions
const globalChannels = new Map<string, RealtimeChannel>();
const globalCallbacks = new Map<string, Set<(payload: any) => void>>();

export function useRealtimeChannel(
  channelName: string,
  options: RealtimeOptions,
  callback: (payload: any) => void
) {
  // Critical validation: ensure all required parameters are valid
  if (!channelName || typeof channelName !== 'string' || !options?.table || typeof options.table !== 'string') {
    console.warn('Invalid parameters for useRealtimeChannel:', { channelName, options });
    return;
  }
  
  const cbRef = useRef(callback);
  const channelKeyRef = useRef(`${channelName}-${options.table}-${options.event || '*'}-${options.filter || ''}`);
  
  // Update callback reference
  cbRef.current = callback;

  useEffect(() => {
    const channelKey = channelKeyRef.current;
    
    // Add callback to global registry
    if (!globalCallbacks.has(channelKey)) {
      globalCallbacks.set(channelKey, new Set());
    }
    globalCallbacks.get(channelKey)!.add(cbRef.current);
    
    // Create channel only if it doesn't exist
    if (!globalChannels.has(channelKey)) {
      console.debug(`ðŸ†• Creating new shared channel: ${channelKey}`);
      
      const channel = supabase
        .channel(channelKey)
        .on(
          'postgres_changes' as any,
          {
            event: options.event || '*',
            schema: options.schema || 'public',
            table: options.table,
            filter: options.filter,
          },
          (payload) => {
            // Notify all callbacks for this channel
            const callbacks = globalCallbacks.get(channelKey);
            if (callbacks) {
              callbacks.forEach(cb => {
                try {
                  cb(payload);
                } catch (error) {
                  console.error('Error in realtime callback:', error);
                }
              });
            }
          }
        );

      // Subscribe once
      channel.subscribe((status) => {
        if (status === 'SUBSCRIBED') {
          console.debug(`âœ… [RT] subscribed â†’ ${options.table}.${options.event || '*'}`);
        } else if (status === 'CHANNEL_ERROR' || status === 'TIMED_OUT' || status === 'CLOSED') {
          console.warn(`âŒ [RT] ${status} â†’ ${options.table}.${options.event || '*'}`);
          // Clean up failed channel
          globalChannels.delete(channelKey);
          globalCallbacks.delete(channelKey);
        }
      });

      globalChannels.set(channelKey, channel);
    } else {
      console.debug(`â™»ï¸ Reusing existing channel: ${channelKey}`);
    }

    return () => {
      const channelKey = channelKeyRef.current;
      console.debug(`ðŸ”Œ Cleaning up callback for: ${channelKey}`);
      
      // Remove callback from global registry
      const callbacks = globalCallbacks.get(channelKey);
      if (callbacks) {
        callbacks.delete(cbRef.current);
        
        // If no more callbacks, clean up the channel
        if (callbacks.size === 0) {
          console.debug(`ðŸ—‘ï¸ Removing unused channel: ${channelKey}`);
          const channel = globalChannels.get(channelKey);
          if (channel) {
            try {
              channel.unsubscribe();
              supabase.removeChannel(channel);
            } catch (error) {
              console.warn('Error during cleanup:', error);
            }
          }
          globalChannels.delete(channelKey);
          globalCallbacks.delete(channelKey);
        }
      }
    };
  }, [channelKeyRef.current]);
}

