sections.content:
/* This is a CSS comment */
p {
  color: navy;
  font-size: 16px;
}

h1 {
  color: green;
}

.my-box {
  padding: 20px; /* 20px of space inside the border */
  border: 1px solid black;
  margin: 40px; /* 40px of space outside the border */
}

.container {
  display: flex;
  justify-content: space-around; /* Distributes items evenly */
  align-items: center; /* Vertically centers items */
}

/* Base styles for mobile */
.container {
  flex-direction: column;
}

/* Styles for tablets and larger */
@media (min-width: 768px) {
  .container {
    flex-direction: row;
  }
}

// Define a function
function greetUser() {
  alert('Welcome to our website!');
}

// Add event listener
document.getElementById('myButton').addEventListener('click', greetUser);
